import React from 'react';

const FIELD_OPTIONS = [
  'New Wallets',
  'Wallet Age',
  'Volume',
  'Smart Wallet Buys',
  'Win Rate',
  'Buy Window',
  'Tracked Wallet Sell',
  'User Holding Token',
  'New Holders',
  'Time Window',
  'Market Cap',
  'Price Retrace',
  'Smart Wallet Re-Buys',
];
const OPERATOR_OPTIONS = ['=', '>', '<', '≥', '≤'];
const UNIT_OPTIONS = ['wallets', 'days', 'SOL', '%', 'minutes', 'event', 'boolean', 'USD'];

export default function LogicBlock({ block, editable = true, onChange, onRemove }) {
  const handleChange = (key, value) => {
    if (onChange) onChange({ ...block, [key]: value });
  };
  return (
    <div className="flex items-center gap-2 bg-zinc-900/30 rounded-lg p-2 mb-1">
      <select
        className="bg-zinc-800 text-zinc-200 rounded px-2 py-1 text-sm font-semibold"
        value={block.field}
        disabled={!editable}
        onChange={e => handleChange('field', e.target.value)}
      >
        {FIELD_OPTIONS.map(opt => (
          <option key={opt} value={opt}>{opt}</option>
        ))}
      </select>
      <select
        className="bg-zinc-800 text-zinc-200 rounded px-2 py-1 text-sm font-semibold"
        value={block.operator}
        disabled={!editable}
        onChange={e => handleChange('operator', e.target.value)}
      >
        {OPERATOR_OPTIONS.map(opt => (
          <option key={opt} value={opt}>{opt}</option>
        ))}
      </select>
      <input
        type="number"
        className="bg-zinc-800 text-zinc-200 rounded px-2 py-1 text-sm font-semibold w-20"
        value={block.value}
        disabled={!editable}
        onChange={e => handleChange('value', Number(e.target.value))}
      />
      {block.unit && (
        <select
          className="bg-zinc-800 text-zinc-200 rounded px-2 py-1 text-sm font-semibold"
          value={block.unit}
          disabled={!editable}
          onChange={e => handleChange('unit', e.target.value)}
        >
          {UNIT_OPTIONS.map(opt => (
            <option key={opt} value={opt}>{opt}</option>
          ))}
        </select>
      )}
      {editable && onRemove && (
        <button className="ml-2 text-red-400 hover:text-red-300 text-xs font-bold" onClick={onRemove}>✕</button>
      )}
    </div>
  );
}
