import React, { useState } from 'react';

const TABS = ['Trending', 'Rising Creators', 'By Meta', 'For You'];

export default function DiscoverTab({ goBack }) {
  const [active, setActive] = useState(0);
  return (
    <div className="w-full max-w-6xl mx-auto py-12">
      {goBack && (
        <button onClick={goBack} className="mb-6 flex items-center text-sm text-blue-400 hover:underline focus:outline-none">
          <span className="mr-2">←</span> Back to Home
        </button>
      )}
      <h2 className="text-3xl font-bold text-gray-100 mb-8 text-center">Discover</h2>
      <div className="flex gap-4 justify-center mb-8">
        {TABS.map((tab, idx) => (
          <button
            key={tab}
            className={`px-4 py-1 rounded-full font-semibold text-base transition-all duration-200 ${active === idx ? 'bg-gradient-to-r from-zinc-800 to-zinc-700 text-white shadow' : 'bg-zinc-900/40 text-gray-400 hover:text-white hover:bg-zinc-800/60'}`}
            onClick={() => setActive(idx)}
          >
            {tab}
          </button>
        ))}
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {[1,2,3].map((i) => (
          <div key={i} className="bg-black/30 backdrop-blur-lg rounded-2xl border border-zinc-500/40 shadow-xl p-8 flex flex-col gap-2">
            <div className="flex items-center gap-2 mb-2">
              <span className="text-sm text-blue-400 font-bold">@AlphaWizard</span>
              <span className="bg-zinc-800 text-gray-300 rounded px-2 py-0.5 text-xs">Trending</span>
            </div>
            <h3 className="text-xl font-bold text-white">Quick Pump</h3>
            <p className="text-gray-300 text-sm mb-2">2+ Smart Wallet Buys, Volume Spike &gt; 200%</p>
            <div className="flex items-center gap-2 text-xs text-green-400 mb-2">Win Rate: 72% <span className="text-gray-400">•</span> Live: 5 tokens</div>
            <button className="mt-auto px-4 py-2 rounded bg-zinc-800 border border-gray-600 text-sm text-white hover:bg-zinc-700 transition">View Flow</button>
          </div>
        ))}
      </div>
    </div>
  );
}
