    >
      {icon && <div className={`mb-3 text-${color}`}>{icon}</div>}
      <div className="text-2xl font-bold text-white flex items-center space-x-2">
        <span className={`text-${color}`}>â€¢</span>
        <span>{title}</span>
      </div>
      <p className="mt-2 text-white/60 text-center">{desc}</p>
    </div>
  );
}
