import React, { useState } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const LOGIC_BLOCKS = [
  { label: 'Smart Buys in 10m', type: 'smart-buys', color: 'green', icon: (
    <svg className="w-5 h-5 text-green-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 8v4l3 3" /></svg>
  ) },
  { label: 'Volume Spike > X%', type: 'volume-spike', color: 'blue', icon: (
    <svg className="w-5 h-5 text-blue-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M3 17v-2a4 4 0 014-4h10a4 4 0 014 4v2" /></svg>
  ) },
  { label: 'Exclude Exit Liquidity', type: 'exclude-exit', color: 'red', icon: (
    <svg className="w-5 h-5 text-red-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" /></svg>
  ) },
  { label: 'GambleFi Tag', type: 'narrative', color: 'purple', icon: (
    <svg className="w-5 h-5 text-purple-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10" /><path strokeLinecap="round" strokeLinejoin="round" d="M12 8v4l3 3" /></svg>
  ) },
];

function LogicBlock({ label, type, color, icon }) {
  const [, drag] = useDrag(() => ({
    type: 'block',
    item: { type, label, color, icon },
  }), [type, label, color, icon]);
  return (
    <div
      ref={drag}
      className={`flex items-center gap-2 bg-neutral-800 rounded-md px-4 py-2 mb-2 cursor-move hover:bg-neutral-700 transition shadow group border-l-4 border-${color}-400`}
      style={{ boxShadow: '0 2px 8px 0 rgba(0,0,0,0.12)' }}
    >
      <span>{icon}</span>
      <span className="font-semibold text-base text-white group-hover:text-white/90 transition">{label}</span>
    </div>
  );
}

function StrategyCanvas({ onDrop, logicBlocks, setLogicBlocks }) {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'block',
    drop: (item) => onDrop(item),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }), [onDrop]);

  const handleRemove = (idx) => {
    setLogicBlocks((prev) => prev.filter((_, i) => i !== idx));
  };

  const handleToggle = (idx) => {
    setLogicBlocks((prev) => prev.map((b, i) => i === idx ? { ...b, logic: b.logic === 'OR' ? 'AND' : 'OR' } : b));
  };

  return (
    <div
      ref={drop}
      className={`min-h-[200px] bg-neutral-900 rounded-lg p-4 text-center border transition-all duration-200 ${isOver ? 'border-2 border-green-400 shadow-green-400/30 shadow-lg' : 'border border-white/10'} ${logicBlocks.length > 0 ? 'shadow-lg' : ''}`}
      style={{ boxShadow: isOver ? '0 0 24px 0 #22d3ee55' : undefined }}
    >
      {logicBlocks.length === 0
        ? (isOver ? <span className="text-green-400 font-bold">Drop to Add Block</span> : <span className="text-white/40">[Drag blocks here]</span>)
        : (
          <div className="flex flex-wrap gap-2 justify-center">
            {logicBlocks.map((b, i) => (
              <span key={i} className={`relative flex items-center gap-2 px-3 py-1 rounded font-semibold text-base shadow bg-${b.color}-900/60 text-${b.color}-200`} style={{ boxShadow: '0 2px 8px 0 rgba(0,0,0,0.18)' }}>
                <span className="mr-1">{b.icon}</span>
                {b.label}
                <button
                  className="ml-1 text-red-400 hover:text-red-600 text-xs font-bold px-1"
                  onClick={() => handleRemove(i)}
                  title="Remove block"
                >×</button>
                {i < logicBlocks.length - 1 && (
                  <button
                    className={`mx-2 px-1 rounded text-xs font-bold border ${b.logic === 'OR' ? 'border-yellow-400 text-yellow-300' : 'border-green-400 text-green-300'} hover:bg-neutral-800 transition`}
                    onClick={() => handleToggle(i)}
                    title="Toggle AND/OR"
                  >
                    {b.logic || 'AND'}
                  </button>
                )}
              </span>
            ))}
          </div>
        )}
    </div>
  );
}

const previewMatches = [
  { name: 'PAWPAW', risk: 'Low', meta: 'GambleFi', roi: '24%' },
  { name: 'BABY BOB', risk: 'Low', meta: 'GambleFi', roi: '34%' },
];

export default function CreateTab() {
  const [logicBlocks, setLogicBlocks] = useState([]);
  const handleDrop = (block) => setLogicBlocks((prev) => [...prev, { ...block, logic: 'AND' }]);

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="flex justify-center gap-8 px-6 md:px-12 py-16 bg-[#0a0a0a] min-h-screen text-white font-sans">
        {/* Logic Blocks Panel */}
        <div className="w-1/4 bg-black/40 rounded-2xl p-6 shadow-md border border-white/10">
          <h2 className="text-xl font-bold mb-4">Logic Blocks</h2>
          {LOGIC_BLOCKS.map((block) => (
            <LogicBlock key={block.type} {...block} />
          ))}
        </div>
        {/* Strategy Canvas */}
        <div className="w-1/3 bg-black/40 rounded-2xl p-6 shadow-md border border-white/10 flex flex-col items-center">
          <h2 className="text-xl font-bold mb-4 text-center">Strategy Flow Canvas</h2>
          <StrategyCanvas onDrop={handleDrop} logicBlocks={logicBlocks} setLogicBlocks={setLogicBlocks} />
          <div className="mt-4 text-base text-white/80 font-semibold">
            {logicBlocks.length > 0 && logicBlocks.map((b, i) => (
              <span key={i}>
                {b.label} {i < logicBlocks.length - 1 ? <span className={b.logic === 'OR' ? 'text-yellow-400 mx-1' : 'text-green-500 mx-1'}>{b.logic || 'AND'}</span> : ''}
              </span>
            ))}
          </div>
        </div>
        {/* Live Preview */}
        <div className="w-1/3 bg-black/40 rounded-2xl p-6 shadow-md border border-white/10 relative">
          <h2 className="text-xl font-bold mb-4 text-center flex items-center justify-center gap-2">
            Live Token Preview
            <span className="ml-2 px-2 py-0.5 rounded-full bg-green-500/20 text-green-300 text-xs font-bold animate-pulse">LIVE</span>
          </h2>
          <div className="space-y-2">
            {previewMatches.map((t) => (
              <div key={t.name} className="bg-neutral-800 p-3 rounded-md flex justify-between items-center border border-green-400/10 shadow hover:shadow-green-400/20 transition">
                <div>
                  <div className="text-white font-semibold text-lg">{t.name}</div>
                  <div className="text-xs text-white/50">Risk: {t.risk} • Meta: {t.meta}</div>
                </div>
                <div className="text-green-400 font-semibold text-lg">{t.roi}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
      <div className="mt-8 flex justify-center gap-4">
        <button className="px-6 py-2 rounded-lg bg-blue-600 hover:bg-blue-500 transition">Save Draft</button>
        <button className="px-6 py-2 rounded-lg bg-green-500 hover:bg-green-400 transition">Publish Flow</button>
      </div>
    </DndProvider>
  );
}
