import React, { useState, useEffect, useRef } from 'react';

const TV_INTERVALS = {
  '1h': '60',
  '4h': '240',
  '1d': 'D',
  '1w': 'W',
};

// Mock live trades feed
const mockLiveTrades = [
  {
    age: '4m',
    winRate: '54% win rate human',
    winRateColor: 'text-pink-400',
    token: 'BTC',
    tokenName: 'Bitcoin',
    amount: 0.881,
    mc: '$20.1K',
    avatar: 'ðŸ·',
    type: 'buy',
  },
  {
    age: '16m',
    winRate: 'human real',
    winRateColor: 'text-red-400',
    token: 'ETH',
    tokenName: 'Ethereum',
