  },
  {
    age: '17m',
    winRate: '54% win rate human',
    winRateColor: 'text-pink-400',
    token: 'SOL',
    tokenName: 'Solana',
    amount: 1.98,
    mc: '$23.9K',
    avatar: 'ðŸ»',
    type: 'buy',
  },
  {
    age: '31m',
    winRate: '54% win rate human',
    winRateColor: 'text-pink-400',
    token: 'AVAX',
    tokenName: 'Avalanche',
    amount: 1.999,
    mc: '$84K',
    avatar: 'ðŸ¦Š',
    type: 'buy',
  },
  {
    age: '33m',
    winRate: 'human real',
    winRateColor: 'text-red-400',
    token: 'BTC',
    tokenName: 'Bitcoin',
    amount: 1.98,
    mc: '$45K',
    avatar: 'ðŸ·',
    type: 'sell',
  },
  {
    age: '40m',
    winRate: 'human real',
    winRateColor: 'text-red-400',
    token: 'ETH',
    tokenName: 'Ethereum',
    amount: 1.979,
    mc: '$704K',
    avatar: 'ðŸ¦',
    type: 'buy',
  },
  {
    age: '46m',
    winRate: 'human real',
    winRateColor: 'text-red-400',
    token: 'SOL',
    tokenName: 'Solana',
    amount: 2.3864,
    mc: '$922K',
    avatar: 'ðŸ»',
    type: 'sell',
  },
  {
    age: '1h',
    winRate: 'Chinese whale',
    winRateColor: 'text-yellow-400',
    token: 'AVAX',
    tokenName: 'Avalanche',
    amount: 20.712,
    mc: '$9.85M',
    avatar: 'ðŸ²',
    type: 'buy',
  },
];

const tokenDetails = {
  BTC: {
    price: 45123.45,
    liquidity: '$1.2B',
    supply: '19.5M',
    volume: '2.5B',
    change24h: 2.5,
    symbol: 'BTC',
    name: 'Bitcoin',
  },
  ETH: {
    price: 2345.67,
    liquidity: '$800M',
    supply: '120M',
    volume: '1.5B',
    change24h: 1.8,
    symbol: 'ETH',
    name: 'Ethereum',
  },
  SOL: {
    price: 98.45,
    liquidity: '$300M',
    supply: '420M',
    volume: '800M',
    change24h: 5.2,
    symbol: 'SOL',
    name: 'Solana',
  },
  AVAX: {
    price: 34.56,
    liquidity: '$120M',
    supply: '377M',
    volume: '400M',
    change24h: 3.1,
    symbol: 'AVAX',
    name: 'Avalanche',
  },
};

export default function StrategyWorkspace({ strategy, goBack }) {
  const [selectedToken, setSelectedToken] = useState(tokenDetails[mockLiveTrades[0].token]);
  const [timeframe, setTimeframe] = useState('1h');
  const [chartType, setChartType] = useState('candles');
  const chartContainerRef = useRef(null);
  const widgetRef = useRef(null);

  useEffect(() => {
    if (!selectedToken) return;
    const script = document.createElement('script');
    script.src = 'https://s3.tradingview.com/tv.js';
    script.async = true;
    script.onload = () => {
      if (selectedToken && chartContainerRef.current) {
        initializeChart();
      }
    };
    document.head.appendChild(script);
    return () => {
      document.head.removeChild(script);
      if (widgetRef.current) {
        const container = document.getElementById(chartContainerRef.current?.id);
        if (container && container.parentNode) {
          widgetRef.current.remove();
        }
        widgetRef.current = null;
      }
    };
    // eslint-disable-next-line
  }, [selectedToken, timeframe, chartType]);

  const initializeChart = () => {
    if (window.TradingView && chartContainerRef.current) {
      if (widgetRef.current) {
        widgetRef.current.remove();
      }
      const symbol = `BINANCE:${selectedToken.symbol}USDT`;
      const interval = TV_INTERVALS[timeframe] || '60';
      const style = chartType === 'candles' ? '1' : '0';
      widgetRef.current = new window.TradingView.widget({
        container_id: chartContainerRef.current.id,
        symbol,
        interval,
        theme: 'dark',
        style,
        locale: 'en',
        toolbar_bg: '#1e222d',
        enable_publishing: false,
        allow_symbol_change: false,
        save_image: false,
        show_popup_button: true,
        popup_width: '1000',
        popup_height: '650',
        autosize: true,
        height: '100%',
        width: '100%',
        hide_side_toolbar: false,
        withdateranges: true,
        hide_volume: false,
        details: true,
        hotlist: true,
        calendar: true,
      });
    }
  };

  // Buy/Sell toast
  const handleTrade = (side) => {
    alert(`${side === 'buy' ? 'Buy' : 'Sell'} order placed for ${selectedToken.symbol}`);
  };

  return (
    <div className="w-full h-screen bg-zinc-900 text-white p-0 flex">
      {/* Left: Live Trades Feed */}
      <div className="w-[320px] bg-zinc-900 border-r border-zinc-800 flex flex-col">
        <div className="px-4 py-3 border-b border-zinc-800 text-lg font-bold">Live Trades</div>
        <div className="flex-1 overflow-y-auto">
          {mockLiveTrades.map((trade, idx) => (
            <div
              key={idx}
              className={`flex items-center gap-3 px-4 py-3 border-b border-zinc-800 cursor-pointer hover:bg-zinc-800 transition ${selectedToken.symbol === trade.token ? 'bg-zinc-800' : ''}`}
              onClick={() => setSelectedToken(tokenDetails[trade.token])}
            >
              <span className="text-2xl">{trade.avatar}</span>
              <div className="flex-1">
                <div className="flex items-center gap-2">
                  <span className={`text-xs font-bold ${trade.winRateColor}`}>{trade.winRate}</span>
                  <span className="text-xs text-gray-400">â€¢ {trade.age} ago</span>
                </div>
                <div className="text-sm font-semibold">{trade.token} <span className="text-gray-400">({trade.tokenName})</span></div>
                <div className="text-xs text-gray-400">Amount: <span className="text-white font-bold">{trade.amount}</span> â€¢ MC: {trade.mc}</div>
              </div>
              <span className={`text-xs font-bold ${trade.type === 'buy' ? 'text-green-400' : 'text-red-400'}`}>{trade.type.toUpperCase()}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Center: Chart */}
      <div className="flex-1 flex flex-col bg-zinc-900">
        {/* Chart Controls */}
        <div className="flex justify-between items-center px-6 py-3 border-b border-zinc-800 bg-zinc-900">
          <div>
            <h3 className="text-xl font-bold">{selectedToken.symbol}/USDT</h3>
            <div className="text-sm text-gray-400">${selectedToken.price.toLocaleString()} â€¢ {selectedToken.change24h}% (24h)</div>
          </div>
          <div className="flex gap-2">
            <button className={`px-3 py-1 rounded ${timeframe === '1h' ? 'bg-blue-500' : 'bg-zinc-800'}`} onClick={() => setTimeframe('1h')}>1h</button>
            <button className={`px-3 py-1 rounded ${timeframe === '4h' ? 'bg-blue-500' : 'bg-zinc-800'}`} onClick={() => setTimeframe('4h')}>4h</button>
            <button className={`px-3 py-1 rounded ${timeframe === '1d' ? 'bg-blue-500' : 'bg-zinc-800'}`} onClick={() => setTimeframe('1d')}>1d</button>
            <button className={`px-3 py-1 rounded ${timeframe === '1w' ? 'bg-blue-500' : 'bg-zinc-800'}`} onClick={() => setTimeframe('1w')}>1w</button>
          </div>
        </div>
        {/* TradingView Chart */}
        <div className="flex-1 bg-zinc-900">
          <div id="tradingview_chart_container" ref={chartContainerRef} className="w-full h-full" />
        </div>
      </div>

      {/* Right: Buy/Sell Panel */}
      <div className="w-[340px] bg-zinc-900 border-l border-zinc-800 flex flex-col p-6">
        <div className="mb-4">
          <div className="flex items-center gap-2 mb-2">
            <span className="text-2xl font-bold">{selectedToken.symbol}</span>
            <span className="text-gray-400">({selectedToken.name})</span>
          </div>
          <div className="text-3xl font-bold mb-1">${selectedToken.price.toLocaleString()}</div>
          <div className="text-xs text-gray-400 mb-2">Liquidity: {selectedToken.liquidity} â€¢ Supply: {selectedToken.supply}</div>
          <div className="text-xs text-gray-400 mb-2">Volume: {selectedToken.volume}</div>
        </div>
        <div className="flex gap-2 mb-4">
          <button className="flex-1 py-2 rounded-lg bg-green-500 text-white font-bold">Buy</button>
          <button className="flex-1 py-2 rounded-lg bg-red-500 text-white font-bold">Sell</button>
        </div>
        <div className="mb-4">
          <label className="block text-xs text-gray-400 mb-1">Amount</label>
          <input type="number" className="w-full bg-zinc-800 rounded-lg px-3 py-2 text-white" placeholder="Enter amount" />
        </div>
        <div className="flex gap-2 mb-4">
          <button className="flex-1 py-2 rounded bg-zinc-800 text-xs">0.01</button>
          <button className="flex-1 py-2 rounded bg-zinc-800 text-xs">0.1</button>
          <button className="flex-1 py-2 rounded bg-zinc-800 text-xs">1</button>
          <button className="flex-1 py-2 rounded bg-zinc-800 text-xs">10</button>
        </div>
        <button className="w-full py-3 rounded-lg bg-gradient-to-r from-green-500 to-blue-500 text-white font-bold mb-2" onClick={() => handleTrade('buy')}>Buy {selectedToken.symbol}</button>
        <button className="w-full py-3 rounded-lg bg-gradient-to-r from-red-500 to-pink-500 text-white font-bold" onClick={() => handleTrade('sell')}>Sell {selectedToken.symbol}</button>
      </div>
    </div>
  );
}
