import React, { useState, useEffect } from 'react';
import ChartArea from './ChartArea';
import { useDashboard } from '../context/DashboardContext';

// Mock live trades data
const liveTrades = [
  { token: { icon: 'üëª', name: 'spam2scam' }, mc: '$892K' },
  { token: { icon: 'üçî', name: 'fried' }, mc: '$892K' },
  { token: { icon: 'üçî', name: 'fried' }, mc: '$782K' },
  { token: { icon: 'üêü', name: 'HYSA' }, mc: '$202K' },
  { token: { icon: 'üì∞', name: 'Wholesome' }, mc: '$132K' },
  { token: { icon: 'üçî', name: 'fried' }, mc: '$814K' },
  { token: { icon: 'üì∞', name: 'Wholesome' }, mc: '$49.2K' },
  { token: { icon: 'üçî', name: 'fried' }, mc: '$814K' },
  { token: { icon: 'ü•ö', name: 'Blorb' }, mc: '$86.8K' },
  { token: { icon: 'ü•ö', name: 'Nlorb' }, mc: '$7.13K' },
  { token: { icon: 'üì∞', name: 'Plotz' }, mc: '$70.1K' },
  { token: { icon: 'üçî', name: 'fried' }, mc: '$953K' },
  { token: { icon: 'üçî', name: 'fried' }, mc: '$1.05M' },
];

// Mock trades table data
const tradesTable = [
  { age: '8h', type: 'Sell', mc: '$4.1K', amount: '539K', usd: '$2.21', trader: '8Un...wCS' },
  { age: '8h', type: 'Sell', mc: '$4.1K', amount: '374K', usd: '$1.53', trader: '87Q...u3C' },
  { age: '8h', type: 'Sell', mc: '$4.12K', amount: '3.98M', usd: '$16.37', trader: 'kqW...QHo' },
  { age: '8h', type: 'Sell', mc: '$4.17K', amount: '9.68M', usd: '$40.39', trader: 'Aow...k92' },
  { age: '8h', type: 'Sell', mc: '$4.21K', amount: '27.91', usd: '$<0.01', trader: 'Fmy...bbA' },
  { age: '8h', type: 'Sell', mc: '$4.22K', amount: '1.36M', usd: '$5.75', trader: '7GP...vST' },
  { age: '8h', type: 'Sell', mc: '$4.23K', amount: '279.1', usd: '$<0.01', trader: '6TP...spz' },
  { age: '8h', type: 'Buy', mc: '$4.23K', amount: '66.7K', usd: '$0.28', trader: 'FNw...Us2' },
  { age: '8h', type: 'Buy', mc: '$147M', amount: '<0.01', usd: '$<0.01', trader: 'a8f...Bv3' },
];

export default function TradeTerminal() {
  const [activeTab, setActiveTab] = useState('spot');
  const [orderType, setOrderType] = useState('limit');
  const [side, setSide] = useState('buy');
  const { activeStrategy } = useDashboard();

  // Real-time feed simulation: add a new trade every 10s
  const FEED_SIZE = 5;
  const [feed, setFeed] = useState(() => {
    // Start with the first FEED_SIZE trades
    return liveTrades.slice(0, FEED_SIZE).map((trade, i) => ({ ...trade, time: `${i * 10}s` }));
  });
  const [nextIndex, setNextIndex] = useState(FEED_SIZE);

  useEffect(() => {
    const interval = setInterval(() => {
      setFeed(prevFeed => {
        // Insert new trade at the top, shift times
        const newTrade = liveTrades[nextIndex % liveTrades.length];
        const updatedFeed = [
          { ...newTrade, time: '0s' },
          ...prevFeed.slice(0, FEED_SIZE - 1).map((t, i) => ({ ...t, time: `${(i + 1) * 10}s` }))
        ];
        return updatedFeed;
      });
      setNextIndex(prev => (prev + 1) % liveTrades.length);
    }, 10000); // 10 seconds
    return () => clearInterval(interval);
  }, [nextIndex]);

  // Mock data for order book
  const orderBook = {
    bids: [
      { price: 45000, amount: 0.5, total: 22500 },
      { price: 44950, amount: 1.2, total: 53940 },
      { price: 44900, amount: 0.8, total: 35920 },
      { price: 44850, amount: 1.5, total: 67275 },
      { price: 44800, amount: 0.3, total: 13440 },
    ],
    asks: [
      { price: 45100, amount: 0.7, total: 31570 },
      { price: 45150, amount: 1.0, total: 45150 },
      { price: 45200, amount: 0.4, total: 18080 },
      { price: 45250, amount: 0.9, total: 40725 },
      { price: 45300, amount: 0.6, total: 27180 },
    ]
  };

  // Mock data for recent trades
  const recentTrades = [
    { price: 45050, amount: 0.2, time: '12:45:23', side: 'buy' },
    { price: 45025, amount: 0.5, time: '12:45:20', side: 'sell' },
    { price: 45075, amount: 0.3, time: '12:45:18', side: 'buy' },
    { price: 45000, amount: 0.8, time: '12:45:15', side: 'sell' },
    { price: 45100, amount: 0.4, time: '12:45:10', side: 'buy' },
  ];

  return (
    <div className="w-full h-screen bg-zinc-900 text-white p-4">
      {/* Header */}
      <div className="flex justify-between items-center mb-4">
        <div className="flex gap-4">
          <button
            className={`px-4 py-2 rounded-lg ${activeTab === 'spot' ? 'bg-blue-500' : 'bg-zinc-800'}`}
            onClick={() => setActiveTab('spot')}
          >
            Spot
          </button>
          <button
            className={`px-4 py-2 rounded-lg ${activeTab === 'futures' ? 'bg-blue-500' : 'bg-zinc-800'}`}
            onClick={() => setActiveTab('futures')}
          >
            Futures
          </button>
          <button
            className={`px-4 py-2 rounded-lg ${activeTab === 'options' ? 'bg-blue-500' : 'bg-zinc-800'}`}
            onClick={() => setActiveTab('options')}
          >
            Options
          </button>
        </div>
        <div className="flex items-center gap-4">
          <div className="text-2xl font-bold">BTC/USDT</div>
          <div className="text-green-500">$45,123.45</div>
          <div className="text-sm text-gray-400">24h: +2.5%</div>
        </div>
      </div>

      {/* Main Content - Three Column Layout */}
      <div className="grid grid-cols-12 gap-4 h-[calc(100vh-8rem)]">
        {/* Left Column - Live Trades Feed */}
        <div className="col-span-3">
          <div className="bg-zinc-800 rounded-lg p-4 h-full">
            <div className="flex items-center justify-between mb-2">
              <h3 className="text-lg font-bold text-white truncate">
                Live Feed
              </h3>
            </div>
            {activeStrategy?.name && activeStrategy.name !== 'None' && (
              <div className="mb-2">
                <span className="inline-block bg-blue-600/20 text-blue-400 text-xs font-semibold px-3 py-1 rounded-full">
                  Active: {activeStrategy.name}
                </span>
              </div>
            )}
            <div className="overflow-y-auto h-[calc(100%-2rem)]">
              <table className="w-full text-sm">
                <thead>
                  <tr className="text-neutral-400 border-b border-neutral-700">
                    <th className="text-left font-normal py-1">Time</th>
                    <th className="text-left font-normal py-1">Token</th>
                    <th className="text-right font-normal py-1">MC</th>
                  </tr>
                </thead>
                <tbody>
                  {feed.map((trade, idx) => (
                    <tr
                      key={idx}
                      className="hover:bg-zinc-700/60 cursor-pointer transition group"
                    >
                      <td className="py-2 text-neutral-400 w-12">{trade.time}</td>
                      <td className="flex items-center gap-2 py-2">
                        <span className="text-xl">{trade.token.icon}</span>
                        <span className="font-semibold text-white truncate max-w-[100px] group-hover:text-blue-400">{trade.token.name}</span>
                      </td>
                      <td className="text-right text-white font-mono">{trade.mc}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        {/* Center Column - TradingView Chart */}
        <div className="col-span-6">
          <div className="bg-zinc-800 rounded-lg p-4 h-full flex flex-col">
            <div className="flex justify-between mb-4">
              <div className="flex gap-2">
                <button className="px-3 py-1 bg-zinc-700 rounded">1m</button>
                <button className="px-3 py-1 bg-zinc-700 rounded">5m</button>
                <button className="px-3 py-1 bg-zinc-700 rounded">15m</button>
                <button className="px-3 py-1 bg-zinc-700 rounded">1h</button>
                <button className="px-3 py-1 bg-zinc-700 rounded">4h</button>
                <button className="px-3 py-1 bg-zinc-700 rounded">1d</button>
              </div>
              <div className="flex gap-2">
                <button className="px-3 py-1 bg-zinc-700 rounded">Candles</button>
                <button className="px-3 py-1 bg-zinc-700 rounded">Line</button>
                <button className="px-3 py-1 bg-zinc-700 rounded">Depth</button>
              </div>
            </div>
            <div className="flex-1 mb-6">
              <ChartArea />
            </div>
            {/* Trades Table */}
            <div className="overflow-x-auto bg-zinc-900/80 rounded-lg border border-zinc-800">
              <table className="w-full text-sm">
                <thead>
                  <tr className="text-neutral-400 border-b border-neutral-800">
                    <th className="text-left font-normal py-2 px-3">Age</th>
                    <th className="text-left font-normal py-2 px-3">Type</th>
                    <th className="text-left font-normal py-2 px-3">MC</th>
                    <th className="text-left font-normal py-2 px-3">Amount</th>
                    <th className="text-left font-normal py-2 px-3">Total USD</th>
                    <th className="text-left font-normal py-2 px-3">Trader</th>
                  </tr>
                </thead>
                <tbody>
                  {tradesTable.map((trade, idx) => (
                    <tr key={idx} className="border-b border-zinc-800 last:border-0 hover:bg-zinc-800/60 transition">
                      <td className="py-2 px-3 text-neutral-400">{trade.age}</td>
                      <td className={
                        `py-2 px-3 font-semibold ${trade.type === 'Buy' ? 'text-green-400' : 'text-red-400'}`
                      }>{trade.type}</td>
                      <td className="py-2 px-3 text-white">{trade.mc}</td>
                      <td className="py-2 px-3 text-white">{trade.amount}</td>
                      <td className={
                        `py-2 px-3 font-mono ${trade.type === 'Buy' ? 'text-green-300' : 'text-red-300'}`
                      }>{trade.usd}</td>
                      <td className="py-2 px-3 text-blue-300 font-mono">{trade.trader}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        {/* Right Column - Trading Terminal */}
        <div className="col-span-3">
          <div className="bg-zinc-800 rounded-lg p-4 h-full">
            <h3 className="text-lg font-semibold mb-4">Trading Terminal</h3>
            <div className="space-y-4">
              {/* Buy/Sell Buttons */}
              <div className="flex gap-2">
                <button
                  className={`flex-1 py-3 rounded-lg font-bold ${
                    side === 'buy' ? 'bg-green-500 text-black' : 'bg-zinc-700 text-white'
                  }`}
                  onClick={() => setSide('buy')}
                >
                  Buy
                </button>
                <button
                  className={`flex-1 py-3 rounded-lg font-bold ${
                    side === 'sell' ? 'bg-red-500 text-white' : 'bg-zinc-700 text-white'
                  }`}
                  onClick={() => setSide('sell')}
                >
                  Sell
                </button>
              </div>

              {/* Order Type */}
              <div className="flex gap-2">
                <button
                  className={`flex-1 py-2 rounded-lg ${
                    orderType === 'limit' ? 'bg-blue-500' : 'bg-zinc-700'
                  }`}
                  onClick={() => setOrderType('limit')}
                >
                  Limit
                </button>
                <button
                  className={`flex-1 py-2 rounded-lg ${
                    orderType === 'market' ? 'bg-blue-500' : 'bg-zinc-700'
                  }`}
                  onClick={() => setOrderType('market')}
                >
                  Market
                </button>
                <button
                  className={`flex-1 py-2 rounded-lg ${
                    orderType === 'stop' ? 'bg-blue-500' : 'bg-zinc-700'
                  }`}
                  onClick={() => setOrderType('stop')}
                >
                  Stop
                </button>
              </div>

              {/* Order Form */}
              <div className="space-y-4">
                <div>
                  <label className="block text-sm text-gray-400 mb-1">Price</label>
                  <input
                    type="number"
                    className="w-full bg-zinc-700 rounded-lg px-3 py-2"
                    placeholder="Enter price"
                  />
                </div>
                <div>
                  <label className="block text-sm text-gray-400 mb-1">Amount</label>
                  <input
                    type="number"
                    className="w-full bg-zinc-700 rounded-lg px-3 py-2"
                    placeholder="Enter amount"
                  />
                </div>
                <div>
                  <label className="block text-sm text-gray-400 mb-1">Total</label>
                  <input
                    type="number"
                    className="w-full bg-zinc-700 rounded-lg px-3 py-2"
                    placeholder="Enter total"
                  />
                </div>
                <button className="w-full py-3 rounded-lg bg-gradient-to-r from-blue-500 to-purple-500 font-bold">
                  {side === 'buy' ? 'Buy BTC' : 'Sell BTC'}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
