import React, { useState, useRef } from 'react';
import { Settings, Sliders, X, Plus, Check, Eye, EyeOff } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import FeedCard from './FeedCard';

const CATEGORY_OPTIONS = [
  'Graduated',
  'Newly Created',
  'About To Graduate',
  'Smart Wallet Behavior',
  'Volume',
  'Market Cap',
  'Holder Growth',
  'Retracement',
  'Token Age',
  'Meta Signal',
  'Social Signal',
  'Custom Strategy',
];

function SectionCard({
  category,
  tokens,
  filters,
  onCategoryChange,
  onAddFilter,
  onOpenSettings,
  onRemoveFilter,
  onDuplicate,
  onClearFilters,
  onSaveStrategy,
  onLoadStrategy,
  buyAmount,
  onDelete,
  isHidden,
  onToggleHidden,
  isStagnant,
}) {
  const navigate = useNavigate();
  // Overlay/modal state: 'none' | 'settings' | 'filters' | 'chatSelector' | 'saveStrategy'
  const [activeOverlay, setActiveOverlay] = useState('none');
  const [newFilter, setNewFilter] = useState({ category: '', condition: '', logic: '', label: '', chats: [] });
  const [tgConnected, setTgConnected] = useState(false);
  const [tgUsername, setTgUsername] = useState('');
  const [socialSignals, setSocialSignals] = useState([]);
  const [availableChats, setAvailableChats] = useState([]);
  const [selectedChats, setSelectedChats] = useState([]);
  const [settingsTab, setSettingsTab] = useState('main'); // 'main' or 'saved'
  const [savedTab, setSavedTab] = useState('mine'); // 'mine', 'community', 'recommended'
  const [savedStrategies, setSavedStrategies] = useState([
    { name: 'Smart Whale Spike', filters: [
      { category: 'Smart Wallet Behavior', condition: '3+ Smart Wallet Buys', logic: 'In last 5 min', label: 'Smart Wallet Behavior: 3+ Smart Wallet Buys (In last 5 min)' },
      { category: 'Volume', condition: 'Volume > $10K', logic: 'In last 10 min', label: 'Volume: Volume > $10K (In last 10 min)' }
    ] },
    { name: 'Telegram + Volume', filters: [
      { category: 'Social Signal', condition: 'Mentioned by @AlphaGroup', logic: 'Within 1h', label: 'Social Signal: Mentioned by @AlphaGroup (Within 1h)' },
      { category: 'Volume', condition: 'Volume > $10K', logic: 'In last 1 hour', label: 'Volume: Volume > $10K (In last 1 hour)' }
    ] },
  ]);
  const communityStrategies = [
    { name: 'Community Trend', filters: [
      { category: 'Meta Signal', condition: 'Narrative: GambleFi', logic: 'Active', label: 'Meta Signal: Narrative: GambleFi (Active)' },
      { category: 'Holder Growth', condition: 'Holders +10%/day', logic: 'Today', label: 'Holder Growth: Holders +10%/day (Today)' }
    ] },
  ];
  const recommendedStrategies = [
    { name: 'Platform Pick: Safe Flow', filters: [
      { category: 'Token Age', condition: '> 7 days', logic: 'Now', label: 'Token Age: > 7 days (Now)' },
      { category: 'Market Cap', condition: 'MC > $1M', logic: 'Now', label: 'Market Cap: MC > $1M (Now)' }
    ] },
  ];

  // Mock filter builder options
  const CATEGORY_OPTS = [
    'Smart Wallet Behavior', 'Volume', 'Market Cap', 'Holder Growth', 'Retracement', 'Token Age', 'Meta Signal', ...(tgConnected ? ['Social Signal'] : []), 'Custom Strategy'
  ];
  const CONDITION_OPTS = {
    'Smart Wallet Behavior': ['Wallets with 60%+ win rate', '3+ Smart Wallet Buys'],
    'Volume': ['Volume > $10K', 'Volume Spike > 50%'],
    'Market Cap': ['MC > $1M', 'MC < $100K'],
    'Holder Growth': ['Holders +10%/day', 'Holders > 100'],
    'Retracement': ['Price retraced 20%', 'New ATH'],
    'Token Age': ['< 1 day', '> 7 days'],
    'Meta Signal': ['Narrative: GambleFi', 'Narrative: AI'],
    'Social Signal': ['Mentioned by @AlphaGroup', 'Telegram group activity'],
    'Custom Strategy': ['Custom logic'],
  };
  const LOGIC_OPTS = {
    'Wallets with 60%+ win rate': ['Buy within 10 min', 'Hold > 1 day'],
    '3+ Smart Wallet Buys': ['In last 5 min', 'In last 1 hour'],
    'Volume > $10K': ['In last 10 min', 'In last 1 hour'],
    'Volume Spike > 50%': ['In last 5 min', 'In last 1 hour'],
    'MC > $1M': ['Now', 'Sustained 1h'],
    'MC < $100K': ['Now'],
    'Holders +10%/day': ['Today'],
    'Holders > 100': ['Now'],
    'Price retraced 20%': ['Since ATH'],
    'New ATH': ['Today'],
    '< 1 day': ['Now'],
    '> 7 days': ['Now'],
    'Narrative: GambleFi': ['Active'],
    'Narrative: AI': ['Active'],
    'Mentioned by @AlphaGroup': ['Within 1h'],
    'Telegram group activity': ['Spike in last 10m'],
    'Custom logic': ['Custom'],
  };

  // Add these new constants after the existing ones
  const SOCIAL_SIGNAL_TYPES = {
    'Mentions': ['> 10 mentions', '> 50 mentions', '> 100 mentions'],
    'Whale Alerts': ['Large buy', 'Large sell', 'Wallet movement'],
    'Community Votes': ['> 100 votes', '> 500 votes', '> 1000 votes'],
    'Group Activity': ['Spike in messages', 'New members', 'Active discussion'],
    'Channel Signals': ['Alpha group mention', 'Influencer alert', 'News update']
  };

  const SOCIAL_SIGNAL_TIME_FRAMES = {
    'Mentions': ['Last 5m', 'Last 15m', 'Last 1h', 'Last 24h'],
    'Whale Alerts': ['Last 5m', 'Last 15m', 'Last 1h'],
    'Community Votes': ['Last 1h', 'Last 24h', 'Last 7d'],
    'Group Activity': ['Last 5m', 'Last 15m', 'Last 1h'],
    'Channel Signals': ['Last 15m', 'Last 1h', 'Last 24h']
  };

  const MOCK_TELEGRAM_CHATS = [
    { id: '1', name: 'Alpha Group', type: 'group', members: 1250, signals: 15 },
    { id: '2', name: 'Whale Alerts', type: 'channel', members: 5000, signals: 8 },
    { id: '3', name: 'Trading Signals', type: 'group', members: 3200, signals: 12 },
    { id: '4', name: 'News Updates', type: 'channel', members: 15000, signals: 5 },
    { id: '5', name: 'Community Votes', type: 'group', members: 800, signals: 20 },
  ];

  const [filterAddedToast, setFilterAddedToast] = useState(null);
  const [justAddedFilterIdx, setJustAddedFilterIdx] = useState(null);
  const filterChipsRef = useRef();

  const handleSaveFilter = () => {
    if (!newFilter.category || !newFilter.condition || !newFilter.logic) return;
    const label = `${newFilter.category}: ${newFilter.condition} (${newFilter.logic})`;
    // Simple param parsing for demo
    let params = {};
    if (newFilter.category === 'Volume') {
      const match = newFilter.condition.match(/>(?:\s*)\$?(\d+(?:\.\d+)?)([KkMm]?)/);
      if (match) {
        let num = parseFloat(match[1]);
        if (match[2].toLowerCase() === 'k') num *= 1000;
        if (match[2].toLowerCase() === 'm') num *= 1000000;
        params.minVolume = num;
      }
      const winMatch = newFilter.logic.match(/(\d+)\s*min/);
      if (winMatch) params.window = parseInt(winMatch[1]);
    }
    if (newFilter.category === 'Smart Wallet Behavior') {
      const match = newFilter.condition.match(/(\d+)\+/);
      if (match) params.minWallets = parseInt(match[1]);
      const winMatch = newFilter.condition.match(/(\d+)%\+/);
      if (winMatch) params.winRate = parseInt(winMatch[1]);
    }
    // Add chats param for Social Signal
    if (newFilter.category === 'Social Signal') {
      params.chats = newFilter.chats;
    }
    onAddFilter({ ...newFilter, label, params });
    setActiveOverlay('none');
    setNewFilter({ category: '', condition: '', logic: '', label: '', chats: [] });
    setFilterAddedToast(label);
    setTimeout(() => {
      if (filterChipsRef.current) {
        filterChipsRef.current.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
      setJustAddedFilterIdx(filters.length); // new filter will be at the end
      setTimeout(() => setJustAddedFilterIdx(null), 1500);
    }, 200);
    setTimeout(() => setFilterAddedToast(null), 1800);
  };

  const handleConnectTelegram = () => {
    // Mock Telegram connection
    setTgConnected(true);
    setTgUsername('alpha_trader');
    setAvailableChats(MOCK_TELEGRAM_CHATS);
    setActiveOverlay('none');
  };

  const handleChatSelect = (chatId) => {
    setSelectedChats(prev => 
      prev.includes(chatId) 
        ? prev.filter(id => id !== chatId)
        : [...prev, chatId]
    );
  };

  const handleSaveStrategy = () => {
    if (!filters || filters.length === 0) return;
    const name = prompt('Enter strategy name:');
    if (!name) return;
    setSavedStrategies(prev => [...prev, { name, filters }]);
    setActiveOverlay('none');
  };

  const handleLoadStrategy = (strategy) => {
    strategy.filters.forEach(filter => onAddFilter(filter));
    setActiveOverlay('none');
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this column?')) {
      onDelete();
      setActiveOverlay('none');
    }
  };

  return (
    <section className={`flex flex-col bg-zinc-900/80 rounded-2xl border border-zinc-800 shadow-xl backdrop-blur-xl min-h-[80vh] max-h-[82vh] transition-all duration-300 w-[260px] transition-shadow ${activeOverlay !== 'none' ? 'ring-2 ring-blue-500/30 scale-[0.98] opacity-90 pulse-shadow' : ''} ${isHidden ? 'opacity-60 grayscale' : ''}`}>
      {/* Header */}
      <header className="sticky top-0 z-10 bg-zinc-900/90 backdrop-blur border-b border-zinc-800 rounded-t-2xl flex items-center justify-between px-3 py-2 select-none">
        <span className="text-base font-semibold text-white capitalize truncate max-w-[120px]">{category}</span>
        <div className="flex items-center gap-2">
          <button className="text-zinc-400 hover:text-blue-400 transition" onClick={() => setActiveOverlay('settings')}>
            <Settings size={18} />
          </button>
          <button className="text-blue-400 hover:text-green-400 transition relative" onClick={() => setActiveOverlay('filters')}>
            <Sliders size={16} />
            {filters && filters.length > 0 && (
              <span className="absolute -top-1 -right-1 bg-green-500 text-white text-[10px] rounded-full px-1.5 py-0.5 font-bold">{filters.length}</span>
            )}
          </button>
          <button
            className={`ml-1 ${isHidden ? 'text-blue-400' : 'text-zinc-400'} hover:text-blue-400 transition`}
            title={isHidden ? 'Unhide column' : 'Hide column'}
            onClick={onToggleHidden}
          >
            {isHidden ? <EyeOff size={16} /> : <Eye size={16} />}
          </button>
        </div>
      </header>
      {/* Filter tags/chips */}
      <div ref={filterChipsRef} className="flex flex-wrap gap-1 px-3 py-2 min-h-[28px]">
        {tgConnected && (
          <span className="bg-green-900/40 text-green-200 px-2 py-0.5 rounded-full text-xs font-semibold flex items-center gap-1">
            <span className="mr-1"></span> Connected as @{tgUsername}
          </span>
        )}
        {filters && filters.length > 0 ? (
          filters.map((f, i) => (
            <span
              key={i}
              className={`bg-blue-900/40 text-blue-200 px-2 py-0.5 rounded-full text-xs font-semibold flex items-center gap-1 transition-all duration-300 ${justAddedFilterIdx === i ? 'ring-2 ring-green-400 animate-pulse' : ''}`}
              title={f.category === 'Social Signal' && f.params?.chats && f.params.chats.length > 0 && availableChats.length > 0
                ? `Chats: ${f.params.chats.map(cid => availableChats.find(c => c.id === cid)?.name || cid).join(', ')}`
                : f.label}
            >
              {f.category === 'Social Signal' && (
                <span className="inline-flex items-center gap-1 text-blue-300">
                  <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="#38bdf8" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" style={{marginRight:2}}><path d="M22 2L11 13"/><path d="M22 2L15 22L11 13L2 9L22 2Z"/></svg>
                  <span>Social Signal</span>
                </span>
              )}
              {f.label || f.summary || f.category}
              <button className="ml-1 text-zinc-400 hover:text-red-400" onClick={() => onRemoveFilter(i)}><X size={12} /></button>
            </span>
          ))
        ) : (
          <span className="text-zinc-500 text-xs">No filters</span>
        )}
      </div>
      {/* Token feed/cards */}
      <div className="flex-1 flex flex-col gap-3 overflow-y-auto token-scrollbar px-2 py-3" style={{ minHeight: 200, maxHeight: '70vh' }}>
        {tokens && tokens.length > 0 ? (
          tokens.map((token, idx) => (
            <FeedCard key={token.name + idx} {...token} buyAmount={buyAmount} />
          ))
        ) : (
          <div className="flex flex-col items-center justify-center h-40 text-zinc-500 text-base font-semibold gap-2">
            <span>No tokens match your filters.</span>
          </div>
        )}
      </div>
      {/* Overlays */}
      {activeOverlay === 'settings' && (
        <div className="fixed inset-0 z-50 flex items-end sm:items-center justify-center bg-black/40 backdrop-blur bg-opacity-60">
          <div className="w-full sm:w-auto bg-zinc-900/95 border border-blue-500/20 rounded-2xl shadow-2xl p-6 min-w-[320px] max-w-[95vw] flex flex-col gap-4 relative animate-slide-up sm:animate-fade-in">
            <button className="absolute top-3 right-3 p-2 rounded-full hover:bg-zinc-800 transition" onClick={() => setActiveOverlay('none')}><X size={20} /></button>
            
            {/* Tabs */}
            <div className="flex gap-2 mb-4">
              <button
                className={`px-4 py-2 rounded-lg font-semibold transition ${settingsTab === 'main' ? 'bg-blue-500 text-white' : 'text-zinc-400 hover:text-white'}`}
                onClick={() => setSettingsTab('main')}
              >
                Settings
              </button>
              <button
                className={`px-4 py-2 rounded-lg font-semibold transition ${settingsTab === 'saved' ? 'bg-blue-500 text-white' : 'text-zinc-400 hover:text-white'}`}
                onClick={() => setSettingsTab('saved')}
              >
                Saved
              </button>
            </div>

            {settingsTab === 'main' ? (
              <div className="space-y-4">
                {/* Category Change */}
                <div>
                  <label className="block text-sm font-semibold text-blue-200 mb-2">Change Category</label>
                  <select
                    className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2"
                    value={category}
                    onChange={e => onCategoryChange(e.target.value)}
                  >
                    {CATEGORY_OPTIONS.map(opt => (
                      <option key={opt} value={opt}>{opt}</option>
                    ))}
                  </select>
                </div>

                {/* Telegram Connection */}
                {!tgConnected ? (
                  <button
                    className="w-full py-2 rounded-lg bg-gradient-to-r from-blue-500 to-purple-500 font-bold text-white shadow hover:scale-105 transition-all"
                    onClick={handleConnectTelegram}
                  >
                    Connect Telegram
                  </button>
                ) : (
                  <div className="space-y-2">
                    <div className="flex items-center justify-between">
                      <span className="text-green-400">Connected as @{tgUsername}</span>
                      <button className="text-red-400 hover:text-red-300" onClick={() => setTgConnected(false)}>Disconnect</button>
                    </div>
                    <button
                      className="w-full py-2 rounded-lg bg-purple-500/20 text-purple-300 border border-purple-500/30 font-semibold hover:bg-purple-500/30 transition"
                      onClick={() => setActiveOverlay('chatSelector')}
                    >
                      Manage Chats
                    </button>
                  </div>
                )}

                {/* Actions */}
                <div className="space-y-2">
                  <button
                    className="w-full py-2 rounded-lg bg-zinc-800 text-zinc-300 border border-zinc-700 font-semibold hover:bg-zinc-700 transition"
                    onClick={() => {
                      onDuplicate();
                      setActiveOverlay('none');
                    }}
                  >
                    Duplicate Section
                  </button>
                  <button
                    className="w-full py-2 rounded-lg bg-red-500/20 text-red-300 border border-red-500/30 font-semibold hover:bg-red-500/30 transition"
                    onClick={() => {
                      onClearFilters();
                      setActiveOverlay('none');
                    }}
                  >
                    Clear Filters
                  </button>
                  {!isStagnant && (
                    <button
                      className="w-full py-2 rounded-lg bg-red-500/20 text-red-300 border border-red-500/30 font-semibold hover:bg-red-500/30 transition"
                      onClick={handleDelete}
                    >
                      Delete Column
                    </button>
                  )}
                </div>
              </div>
            ) : (
              <div className="space-y-4">
                {/* Saved Strategies Tabs */}
                <div className="flex gap-2 mb-4">
                  <button
                    className={`px-4 py-2 rounded-lg font-semibold transition ${savedTab === 'mine' ? 'bg-blue-500 text-white' : 'text-zinc-400 hover:text-white'}`}
                    onClick={() => setSavedTab('mine')}
                  >
                    Mine
                  </button>
                  <button
                    className={`px-4 py-2 rounded-lg font-semibold transition ${savedTab === 'community' ? 'bg-blue-500 text-white' : 'text-zinc-400 hover:text-white'}`}
                    onClick={() => setSavedTab('community')}
                  >
                    Community
                  </button>
                  <button
                    className={`px-4 py-2 rounded-lg font-semibold transition ${savedTab === 'recommended' ? 'bg-blue-500 text-white' : 'text-zinc-400 hover:text-white'}`}
                    onClick={() => setSavedTab('recommended')}
                  >
                    Recommended
                  </button>
                </div>

                {/* Strategy List */}
                <div className="space-y-2 max-h-[300px] overflow-y-auto">
                  {(savedTab === 'mine' ? savedStrategies :
                    savedTab === 'community' ? communityStrategies :
                    recommendedStrategies).map((strategy, i) => (
                    <div
                      key={i}
                      className="p-3 rounded-lg bg-zinc-800/50 border border-zinc-700 hover:border-blue-500/30 transition cursor-pointer"
                      onClick={() => handleLoadStrategy(strategy)}
                    >
                      <div className="font-semibold text-white">{strategy.name}</div>
                      <div className="text-sm text-zinc-400 mt-1">
                        {strategy.filters.map(f => f.label).join(' + ')}
                      </div>
                    </div>
                  ))}
                </div>

                {/* Save Current Strategy */}
                <button
                  className="w-full py-2 rounded-lg bg-gradient-to-r from-blue-500 to-green-500 font-bold text-white shadow hover:scale-105 transition-all mt-4"
                  onClick={handleSaveStrategy}
                  disabled={!filters || filters.length === 0}
                >
                  Save Current Strategy
                </button>
              </div>
            )}
          </div>
        </div>
      )}
      {activeOverlay === 'filters' && (
        <div className="fixed inset-0 z-50 flex items-end sm:items-center justify-center bg-black/40 backdrop-blur bg-opacity-60">
          <div className="w-full sm:w-auto bg-zinc-900/95 border border-blue-500/20 rounded-2xl shadow-2xl p-6 min-w-[320px] max-w-[95vw] flex flex-col gap-4 relative animate-slide-up sm:animate-fade-in">
            <button className="absolute top-3 right-3 p-2 rounded-full hover:bg-zinc-800 transition" onClick={() => setActiveOverlay('none')}><X size={20} /></button>
            <div className="font-bold text-lg text-blue-300 mb-2">Add Filter</div>
            <label className="font-semibold text-blue-200 mb-1">Category:</label>
            <select
              className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2 mb-2"
              value={newFilter.category}
              onChange={e => setNewFilter(f => ({ ...f, category: e.target.value, condition: '', logic: '' }))}
            >
              <option value="">Select category</option>
              {CATEGORY_OPTS.map(opt => <option key={opt} value={opt}>{opt}</option>)}
            </select>
            {newFilter.category && (
              <>
                <label className="font-semibold text-blue-200 mb-1">Condition:</label>
                <select
                  className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2 mb-2"
                  value={newFilter.condition}
                  onChange={e => setNewFilter(f => ({ ...f, condition: e.target.value, logic: '' }))}
                >
                  <option value="">Select condition</option>
                  {(CONDITION_OPTS[newFilter.category] || []).map(opt => <option key={opt} value={opt}>{opt}</option>)}
                </select>
                {/* If Social Signal, show chat selector */}
                {newFilter.category === 'Social Signal' && tgConnected && (
                  <div className="mb-2">
                    <label className="font-semibold text-blue-200 mb-1">Select Telegram Chats:</label>
                    <div className="flex flex-wrap gap-2">
                      {availableChats.map(chat => (
                        <button
                          key={chat.id}
                          type="button"
                          className={`px-2 py-1 rounded-full border text-xs font-semibold transition ${newFilter.chats.includes(chat.id) ? 'bg-blue-500 text-white border-blue-500' : 'bg-zinc-800 text-blue-300 border-zinc-700 hover:bg-blue-700/30'}`}
                          onClick={() => setNewFilter(f => ({
                            ...f,
                            chats: f.chats.includes(chat.id)
                              ? f.chats.filter(id => id !== chat.id)
                              : [...f.chats, chat.id]
                          }))}
                        >
                          {chat.name}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
              </>
            )}
            {newFilter.condition && (
              <>
                <label className="font-semibold text-blue-200 mb-1">Logic:</label>
                <select
                  className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2 mb-2"
                  value={newFilter.logic}
                  onChange={e => setNewFilter(f => ({ ...f, logic: e.target.value }))}
                >
                  <option value="">Select logic</option>
                  {(LOGIC_OPTS[newFilter.condition] || []).map(opt => <option key={opt} value={opt}>{opt}</option>)}
                </select>
              </>
            )}
            <button
              className="w-full py-2 rounded-lg bg-gradient-to-r from-blue-500 to-green-500 font-bold text-white shadow hover:scale-105 transition-all mt-2"
              onClick={handleSaveFilter}
              disabled={!newFilter.category || !newFilter.condition || !newFilter.logic}
            >
              Save Filter
            </button>
          </div>
        </div>
      )}
      {activeOverlay === 'chatSelector' && (
        <div className="fixed inset-0 z-50 flex items-end sm:items-center justify-center bg-black/40 backdrop-blur bg-opacity-60">
          <div className="w-full sm:w-auto bg-zinc-900/95 border border-blue-500/20 rounded-2xl shadow-2xl p-6 min-w-[320px] max-w-[95vw] flex flex-col gap-4 relative animate-slide-up sm:animate-fade-in">
            <button className="absolute top-3 right-3 p-2 rounded-full hover:bg-zinc-800 transition" onClick={() => setActiveOverlay('none')}><X size={20} /></button>
            <div className="font-bold text-lg text-blue-300 mb-2">Select Telegram Chats</div>
            <div className="space-y-2 max-h-[300px] overflow-y-auto">
              {availableChats.map(chat => (
                <button
                  key={chat.id}
                  className={`w-full p-3 rounded-lg text-left transition ${
                    selectedChats.includes(chat.id)
                      ? 'bg-blue-500/20 border border-blue-500/30'
                      : 'bg-zinc-800/50 border border-zinc-700 hover:border-blue-500/30'
                  }`}
                  onClick={() => handleChatSelect(chat.id)}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <div className="font-semibold text-white">{chat.name}</div>
                      <div className="text-sm text-zinc-400">
                        {chat.type === 'group' ? 'Group' : 'Channel'}  {chat.members.toLocaleString()} members
                      </div>
                    </div>
                    {selectedChats.includes(chat.id) && (
                      <Check className="text-blue-400" size={20} />
                    )}
                  </div>
                </button>
              ))}
            </div>
            <button
              className="w-full py-2 rounded-lg bg-gradient-to-r from-blue-500 to-green-500 font-bold text-white shadow hover:scale-105 transition-all mt-4"
              onClick={() => setActiveOverlay('none')}
            >
              Done
            </button>
          </div>
        </div>
      )}
      {filterAddedToast && (
        <div className="fixed bottom-8 right-8 z-50 bg-green-600/90 text-white px-4 py-2 rounded-xl shadow-lg font-semibold animate-fade-in">
          <span>Filter added:</span>
          <span className="ml-2 font-mono">{filterAddedToast}</span>
        </div>
      )}
    </section>
  );
}

export default SectionCard;
