import React, { useState, useRef } from 'react';
import { Settings, Sliders, X, Plus, Check } from 'lucide-react';
import FeedCard from './FeedCard';

const CATEGORY_OPTIONS = [
  'Graduated',
  'Newly Created',
  'About To Graduate',
  'Smart Wallet Behavior',
  'Volume',
  'Market Cap',
  'Holder Growth',
  'Retracement',
  'Token Age',
  'Meta Signal',
  'Social Signal',
  'Custom Strategy',
];

export default function SectionCard({
  category,
  tokens,
  filters,
  onCategoryChange,
  onAddFilter,
  onOpenSettings,
  onRemoveFilter,
  onDuplicate,
  onClearFilters,
  onSaveStrategy,
  onLoadStrategy,
  onTokenClick,
  buyAmount,
}) {
  // Overlay/modal state: 'none' | 'settings' | 'filters' | 'chatSelector' | 'saveStrategy'
  const [activeOverlay, setActiveOverlay] = useState('none');
  const [newFilter, setNewFilter] = useState({ category: '', condition: '', logic: '', label: '' });
  const [tgConnected, setTgConnected] = useState(false);
  const [tgUsername, setTgUsername] = useState('');
  const [socialSignals, setSocialSignals] = useState([]);
  const [availableChats, setAvailableChats] = useState([]);
  const [selectedChats, setSelectedChats] = useState([]);
  const [settingsTab, setSettingsTab] = useState('main'); // 'main' or 'saved'
  const [savedTab, setSavedTab] = useState('mine'); // 'mine', 'community', 'recommended'
  const [savedStrategies, setSavedStrategies] = useState([
    { name: 'Smart Whale Spike', filters: [
      { category: 'Smart Wallet Behavior', condition: '3+ Smart Wallet Buys', logic: 'In last 5 min', label: 'Smart Wallet Behavior: 3+ Smart Wallet Buys (In last 5 min)' },
      { category: 'Volume', condition: 'Volume > $10K', logic: 'In last 10 min', label: 'Volume: Volume > $10K (In last 10 min)' }
    ] },
    { name: 'Telegram + Volume', filters: [
      { category: 'Social Signal', condition: 'Mentioned by @AlphaGroup', logic: 'Within 1h', label: 'Social Signal: Mentioned by @AlphaGroup (Within 1h)' },
      { category: 'Volume', condition: 'Volume > $10K', logic: 'In last 1 hour', label: 'Volume: Volume > $10K (In last 1 hour)' }
    ] },
  ]);
  const communityStrategies = [
    { name: 'Community Trend', filters: [
      { category: 'Meta Signal', condition: 'Narrative: GambleFi', logic: 'Active', label: 'Meta Signal: Narrative: GambleFi (Active)' },
      { category: 'Holder Growth', condition: 'Holders +10%/day', logic: 'Today', label: 'Holder Growth: Holders +10%/day (Today)' }
    ] },
  ];
  const recommendedStrategies = [
    { name: 'Platform Pick: Safe Flow', filters: [
      { category: 'Token Age', condition: '> 7 days', logic: 'Now', label: 'Token Age: > 7 days (Now)' },
      { category: 'Market Cap', condition: 'MC > $1M', logic: 'Now', label: 'Market Cap: MC > $1M (Now)' }
    ] },
  ];

  // Mock filter builder options
  const CATEGORY_OPTS = [
    'Smart Wallet Behavior', 'Volume', 'Market Cap', 'Holder Growth', 'Retracement', 'Token Age', 'Meta Signal', ...(tgConnected ? ['Social Signal'] : []), 'Custom Strategy'
  ];
  const CONDITION_OPTS = {
    'Smart Wallet Behavior': ['Wallets with 60%+ win rate', '3+ Smart Wallet Buys'],
    'Volume': ['Volume > $10K', 'Volume Spike > 50%'],
    'Market Cap': ['MC > $1M', 'MC < $100K'],
    'Holder Growth': ['Holders +10%/day', 'Holders > 100'],
    'Retracement': ['Price retraced 20%', 'New ATH'],
    'Token Age': ['< 1 day', '> 7 days'],
    'Meta Signal': ['Narrative: GambleFi', 'Narrative: AI'],
    'Social Signal': ['Mentioned by @AlphaGroup', 'Telegram group activity'],
    'Custom Strategy': ['Custom logic'],
  };
  const LOGIC_OPTS = {
    'Wallets with 60%+ win rate': ['Buy within 10 min', 'Hold > 1 day'],
    '3+ Smart Wallet Buys': ['In last 5 min', 'In last 1 hour'],
    'Volume > $10K': ['In last 10 min', 'In last 1 hour'],
    'Volume Spike > 50%': ['In last 5 min', 'In last 1 hour'],
    'MC > $1M': ['Now', 'Sustained 1h'],
    'MC < $100K': ['Now'],
    'Holders +10%/day': ['Today'],
    'Holders > 100': ['Now'],
    'Price retraced 20%': ['Since ATH'],
    'New ATH': ['Today'],
    '< 1 day': ['Now'],
    '> 7 days': ['Now'],
    'Narrative: GambleFi': ['Active'],
    'Narrative: AI': ['Active'],
    'Mentioned by @AlphaGroup': ['Within 1h'],
    'Telegram group activity': ['Spike in last 10m'],
    'Custom logic': ['Custom'],
  };

  // Add these new constants after the existing ones
  const SOCIAL_SIGNAL_TYPES = {
    'Mentions': ['> 10 mentions', '> 50 mentions', '> 100 mentions'],
    'Whale Alerts': ['Large buy', 'Large sell', 'Wallet movement'],
    'Community Votes': ['> 100 votes', '> 500 votes', '> 1000 votes'],
    'Group Activity': ['Spike in messages', 'New members', 'Active discussion'],
    'Channel Signals': ['Alpha group mention', 'Influencer alert', 'News update']
  };

  const SOCIAL_SIGNAL_TIME_FRAMES = {
    'Mentions': ['Last 5m', 'Last 15m', 'Last 1h', 'Last 24h'],
    'Whale Alerts': ['Last 5m', 'Last 15m', 'Last 1h'],
    'Community Votes': ['Last 1h', 'Last 24h', 'Last 7d'],
    'Group Activity': ['Last 5m', 'Last 15m', 'Last 1h'],
    'Channel Signals': ['Last 15m', 'Last 1h', 'Last 24h']
  };

  const MOCK_TELEGRAM_CHATS = [
    { id: '1', name: 'Alpha Group', type: 'group', members: 1250, signals: 15 },
    { id: '2', name: 'Whale Alerts', type: 'channel', members: 5000, signals: 8 },
    { id: '3', name: 'Trading Signals', type: 'group', members: 3200, signals: 12 },
    { id: '4', name: 'News Updates', type: 'channel', members: 15000, signals: 5 },
    { id: '5', name: 'Community Votes', type: 'group', members: 800, signals: 20 },
  ];

  const [filterAddedToast, setFilterAddedToast] = useState(null);
  const [justAddedFilterIdx, setJustAddedFilterIdx] = useState(null);
  const filterChipsRef = useRef();

  const handleSaveFilter = () => {
    if (!newFilter.category || !newFilter.condition || !newFilter.logic) return;
    const label = `${newFilter.category}: ${newFilter.condition} (${newFilter.logic})`;
    onAddFilter({ ...newFilter, label });
    setActiveOverlay('none');
    setNewFilter({ category: '', condition: '', logic: '', label: '' });
    setFilterAddedToast(label);
    setTimeout(() => {
      if (filterChipsRef.current) {
        filterChipsRef.current.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
      setJustAddedFilterIdx(filters.length); // new filter will be at the end
      setTimeout(() => setJustAddedFilterIdx(null), 1500);
    }, 200);
    setTimeout(() => setFilterAddedToast(null), 1800);
  };

  const handleSaveStrategy = () => {
    const name = prompt('Name your strategy:');
    if (!name) return;
    setSavedStrategies(prev => [...prev, { name, filters }]);
    alert('Strategy saved!');
    setActiveOverlay('none');
  };

  const handleApplyStrategy = (strategy) => {
    // Replace all filters with those from the strategy
    strategy && strategy.filters && strategy.filters.forEach(f => onAddFilter(f));
  };

  const handleTelegramConnect = async () => {
    // Mock Telegram connection flow
    const username = prompt('Enter your Telegram username:');
    if (username) {
      setTgUsername(username);
      setTgConnected(true);
      setAvailableChats(MOCK_TELEGRAM_CHATS);
      setActiveOverlay('chatSelector');
    }
  };

  const handleChatSelection = (chatId) => {
    setSelectedChats(prev => {
      if (prev.includes(chatId)) {
        return prev.filter(id => id !== chatId);
      }
      return [...prev, chatId];
    });
  };

  const handleSaveChatSelection = () => {
    const selectedChatData = availableChats.filter(chat => selectedChats.includes(chat.id));
    setSocialSignals(selectedChatData.map(chat => ({
      type: chat.type === 'group' ? 'Group Activity' : 'Channel Signals',
      count: chat.signals,
      timeframe: 'Last 1h',
      chatName: chat.name
    })));
    setActiveOverlay('none');
  };

  return (
    <section className={`flex flex-col bg-zinc-900/80 rounded-2xl border border-zinc-800 shadow-xl backdrop-blur-xl min-h-[80vh] max-h-[82vh] transition-all duration-300 w-[260px] transition-shadow ${activeOverlay !== 'none' ? 'ring-2 ring-blue-500/30 scale-[0.98] opacity-90 pulse-shadow' : ''}`}>
      {/* Header */}
      <header className="sticky top-0 z-10 bg-zinc-900/90 backdrop-blur border-b border-zinc-800 rounded-t-2xl flex items-center justify-between px-3 py-2 select-none">
        <span className="text-base font-semibold text-white capitalize truncate max-w-[120px]">{category}</span>
        <div className="flex items-center gap-2">
          <button className="text-zinc-400 hover:text-blue-400 transition" onClick={() => setActiveOverlay('settings')}>
            <Settings size={18} />
          </button>
          <button className="text-blue-400 hover:text-green-400 transition relative">
            <Sliders size={16} />
            {filters && filters.length > 0 && (
              <span className="absolute -top-1 -right-1 bg-green-500 text-white text-[10px] rounded-full px-1.5 py-0.5 font-bold">{filters.length}</span>
            )}
          </button>
        </div>
      </header>
      {/* Filter tags/chips */}
      <div ref={filterChipsRef} className="flex flex-wrap gap-1 px-3 py-2 min-h-[28px]">
        {tgConnected && (
          <span className="bg-green-900/40 text-green-200 px-2 py-0.5 rounded-full text-xs font-semibold flex items-center gap-1">
            <span className="mr-1">📣</span> Connected as @{tgUsername}
          </span>
        )}
        {filters && filters.length > 0 ? (
          filters.map((f, i) => (
            <span
              key={i}
              className={`bg-blue-900/40 text-blue-200 px-2 py-0.5 rounded-full text-xs font-semibold flex items-center gap-1 transition-all duration-300 ${justAddedFilterIdx === i ? 'ring-2 ring-green-400 animate-pulse' : ''}`}
            >
              {f.label || f.summary || f.category}
              <button className="ml-1 text-zinc-400 hover:text-red-400" onClick={() => onRemoveFilter(i)}><X size={12} /></button>
            </span>
          ))
        ) : (
          <span className="text-zinc-500 text-xs">No filters</span>
        )}
      </div>
      {/* Token feed/cards */}
      <div className="flex-1 flex flex-col gap-3 overflow-y-auto token-scrollbar px-2 py-3" style={{ minHeight: 200, maxHeight: '70vh' }}>
        {tokens && tokens.length > 0 ? (
          tokens.map((token, idx) => (
            <FeedCard key={token.name + idx} {...token} buyAmount={buyAmount} onClick={() => onTokenClick(token)} />
          ))
        ) : (
          <div className="flex flex-col items-center justify-center h-40 text-zinc-500 text-base font-semibold gap-2">
            <span>No tokens match your filters.</span>
          </div>
        )}
      </div>
      {/* Overlays */}
      {activeOverlay === 'filters' && (
        <div className="fixed inset-0 z-50 flex items-end sm:items-center justify-center bg-black/40 backdrop-blur bg-opacity-60">
          <div className="w-full sm:w-auto bg-zinc-900/95 border border-blue-500/20 rounded-2xl shadow-2xl p-6 min-w-[320px] max-w-[95vw] flex flex-col gap-4 relative animate-slide-up sm:animate-fade-in">
            <button className="absolute top-3 right-3 p-2 rounded-full hover:bg-zinc-800 transition" onClick={() => setActiveOverlay('none')}><X size={20} /></button>
            <div className="font-bold text-lg text-blue-300 mb-2">Add Filter</div>
            <label className="font-semibold text-blue-200 mb-1">Category:</label>
            <select
              className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2 mb-2"
              value={newFilter.category}
              onChange={e => setNewFilter(f => ({ ...f, category: e.target.value, condition: '', logic: '' }))}
            >
              <option value="">Select category</option>
              {CATEGORY_OPTS.map(opt => <option key={opt} value={opt}>{opt}</option>)}
            </select>
            {newFilter.category === 'Social Signal' && (
              <>
                <label className="font-semibold text-blue-200 mb-1">Signal Type:</label>
                <select
                  className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2 mb-2"
                  value={newFilter.signalType || ''}
                  onChange={e => setNewFilter(f => ({ ...f, signalType: e.target.value, timeframe: '' }))}
                >
                  <option value="">Select signal type</option>
                  {Object.keys(SOCIAL_SIGNAL_TYPES).map(type => (
                    <option key={type} value={type}>{type}</option>
                  ))}
                </select>
                {newFilter.signalType && (
                  <>
                    <label className="font-semibold text-blue-200 mb-1">Condition:</label>
                    <select
                      className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2 mb-2"
                      value={newFilter.condition}
                      onChange={e => setNewFilter(f => ({ ...f, condition: e.target.value, timeframe: '' }))}
                    >
                      <option value="">Select condition</option>
                      {SOCIAL_SIGNAL_TYPES[newFilter.signalType].map(opt => (
                        <option key={opt} value={opt}>{opt}</option>
                      ))}
                    </select>
                  </>
                )}
                {newFilter.condition && (
                  <>
                    <label className="font-semibold text-blue-200 mb-1">Time Frame:</label>
                    <select
                      className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2 mb-2"
                      value={newFilter.timeframe}
                      onChange={e => setNewFilter(f => ({ ...f, timeframe: e.target.value }))}
                    >
                      <option value="">Select time frame</option>
                      {SOCIAL_SIGNAL_TIME_FRAMES[newFilter.signalType].map(opt => (
                        <option key={opt} value={opt}>{opt}</option>
                      ))}
                    </select>
                  </>
                )}
              </>
            )}
            <button
              className="w-full py-2 rounded-lg bg-gradient-to-r from-blue-500 to-green-500 font-bold text-white shadow hover:scale-105 transition-all mt-2"
              onClick={handleSaveFilter}
              disabled={!newFilter.category || !newFilter.condition || !newFilter.logic}
            >
              Save Filter
            </button>
          </div>
        </div>
      )}
      {activeOverlay === 'settings' && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/40 backdrop-blur animate-fade-in">
          <div className="w-full sm:w-auto bg-zinc-900/95 border border-blue-500/20 rounded-2xl shadow-2xl p-6 min-w-[340px] max-w-[95vw] flex flex-col gap-4 relative animate-fade-in">
            <button className="absolute top-3 right-3 p-2 rounded-full hover:bg-zinc-800 transition" onClick={() => setActiveOverlay('none')}><X size={20} /></button>
            <div className="flex gap-4 mb-2">
              <button className={`font-bold text-lg ${settingsTab === 'main' ? 'text-blue-300' : 'text-zinc-400'}`} onClick={() => setSettingsTab('main')}>Main</button>
              <button className={`font-bold text-lg ${settingsTab === 'saved' ? 'text-blue-300' : 'text-zinc-400'}`} onClick={() => setSettingsTab('saved')}>Saved</button>
            </div>
            {settingsTab === 'main' && <>
              {/* Telegram Connect */}
              {!tgConnected ? (
                <button 
                  className="w-full py-2 rounded-lg bg-gradient-to-r from-blue-500 to-green-500 font-bold text-white shadow hover:scale-105 transition-all mb-2 flex items-center justify-center gap-2"
                  onClick={handleTelegramConnect}
                >
                  <span>📣</span> Connect Telegram
                </button>
              ) : (
                <div className="w-full py-2 rounded-lg bg-green-900/40 text-green-200 font-bold flex items-center justify-between px-3 mb-2">
                  <div className="flex items-center gap-2">
                    <span>📣</span> Connected as @{tgUsername}
                  </div>
                  <button 
                    className="text-xs bg-red-500/20 text-red-200 px-2 py-1 rounded hover:bg-red-500/30 transition"
                    onClick={() => {
                      setTgConnected(false);
                      setTgUsername('');
                      setSocialSignals([]);
                      setSelectedChats([]);
                    }}
                  >
                    Disconnect
                  </button>
                </div>
              )}
              {/* Change Category */}
              <div>
                <label className="font-semibold text-blue-200 mb-1 block">Change Category:</label>
                <select
                  className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2 mt-1"
                  value={category}
                  onChange={e => { onCategoryChange(e.target.value); setActiveOverlay('none'); }}
                >
                  {CATEGORY_OPTIONS.map(opt => <option key={opt} value={opt}>{opt}</option>)}
                </select>
              </div>
              {/* Add Filter */}
              <button className="w-full py-2 rounded-lg bg-gradient-to-r from-blue-500 to-green-500 font-bold text-white shadow hover:scale-105 transition-all flex items-center justify-center gap-2" onClick={() => setActiveOverlay('filters')}><Plus size={16} /> Add Filter</button>
              {/* Save/Load/Duplicate/Clear */}
              <div className="flex flex-col gap-2 mt-2">
                <button className="w-full py-2 rounded bg-zinc-800 text-blue-200 font-semibold hover:bg-blue-900/30 transition" onClick={() => setActiveOverlay('saveStrategy')}>Save as Template</button>
                <button className="w-full py-2 rounded bg-zinc-800 text-blue-200 font-semibold hover:bg-blue-900/30 transition" onClick={onLoadStrategy}>Load Saved Strategy</button>
                <button className="w-full py-2 rounded bg-zinc-800 text-blue-200 font-semibold hover:bg-blue-900/30 transition" onClick={onDuplicate}>Duplicate Section</button>
                <button className="w-full py-2 rounded bg-zinc-800 text-red-300 font-semibold hover:bg-red-900/30 transition" onClick={onClearFilters}>Clear Filters</button>
              </div>
            </>}
            {settingsTab === 'saved' && <>
              <div className="flex gap-2 mb-2">
                <button className={`px-3 py-1 rounded-full font-semibold text-sm ${savedTab === 'mine' ? 'bg-blue-500/30 text-blue-200' : 'bg-zinc-800 text-zinc-400'}`} onClick={() => setSavedTab('mine')}>Mine</button>
                <button className={`px-3 py-1 rounded-full font-semibold text-sm ${savedTab === 'community' ? 'bg-blue-500/30 text-blue-200' : 'bg-zinc-800 text-zinc-400'}`} onClick={() => setSavedTab('community')}>Community</button>
                <button className={`px-3 py-1 rounded-full font-semibold text-sm ${savedTab === 'recommended' ? 'bg-blue-500/30 text-blue-200' : 'bg-zinc-800 text-zinc-400'}`} onClick={() => setSavedTab('recommended')}>Recommended</button>
              </div>
              <div className="flex flex-col gap-2 max-h-64 overflow-y-auto">
                {(savedTab === 'mine' ? savedStrategies : savedTab === 'community' ? communityStrategies : recommendedStrategies).map((strat, i) => (
                  <div key={i} className="bg-zinc-800/80 rounded-xl p-3 flex flex-col gap-1 border border-blue-500/10">
                    <div className="font-bold text-blue-200 text-sm mb-1">{strat.name}</div>
                    <div className="flex flex-wrap gap-1 mb-2">
                      {strat.filters.map((f, j) => (
                        <span key={j} className="bg-blue-900/40 text-blue-200 px-2 py-0.5 rounded-full text-xs font-semibold">{f.label}</span>
                      ))}
                    </div>
                    <button className="w-full py-1 rounded bg-gradient-to-r from-blue-500 to-green-500 text-white font-bold text-xs hover:scale-105 transition" onClick={() => handleApplyStrategy(strat)}>Apply</button>
                  </div>
                ))}
                {((savedTab === 'mine' ? savedStrategies : savedTab === 'community' ? communityStrategies : recommendedStrategies).length === 0) && (
                  <div className="text-zinc-500 text-sm text-center py-6">No strategies found.</div>
                )}
              </div>
            </>}
          </div>
        </div>
      )}
      {activeOverlay === 'chatSelector' && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/40 backdrop-blur animate-fade-in">
          <div className="w-full sm:w-auto bg-zinc-900/95 border border-blue-500/20 rounded-2xl shadow-2xl p-6 min-w-[400px] max-w-[95vw] flex flex-col gap-4 relative animate-fade-in">
            <button className="absolute top-3 right-3 p-2 rounded-full hover:bg-zinc-800 transition" onClick={() => setActiveOverlay('none')}><X size={20} /></button>
            <div className="font-bold text-lg text-blue-300 mb-2">Select Telegram Chats</div>
            <div className="text-sm text-zinc-400 mb-4">Choose which chats to monitor for signals</div>
            <div className="flex flex-col gap-2 max-h-[400px] overflow-y-auto">
              {availableChats.map(chat => (
                <div
                  key={chat.id}
                  className={`flex items-center justify-between p-3 rounded-lg border transition-all cursor-pointer ${
                    selectedChats.includes(chat.id)
                      ? 'bg-blue-500/20 border-blue-500/30'
                      : 'bg-zinc-800/50 border-zinc-700 hover:bg-zinc-800'
                  }`}
                  onClick={() => handleChatSelection(chat.id)}
                >
                  <div className="flex items-center gap-3">
                    <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                      chat.type === 'group' ? 'bg-purple-500/20' : 'bg-blue-500/20'
                    }`}>
                      {chat.type === 'group' ? '👥' : '📢'}
                    </div>
                    <div>
                      <div className="font-semibold text-white">{chat.name}</div>
                      <div className="text-xs text-zinc-400">
                        {chat.type === 'group' ? 'Group' : 'Channel'} • {chat.members.toLocaleString()} members
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="text-xs text-zinc-400">{chat.signals} signals/h</span>
                    {selectedChats.includes(chat.id) && (
                      <div className="w-5 h-5 rounded-full bg-blue-500 flex items-center justify-center">
                        <Check size={14} className="text-white" />
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
            <button
              className="w-full py-2 rounded-lg bg-gradient-to-r from-blue-500 to-green-500 font-bold text-white shadow hover:scale-105 transition-all mt-2"
              onClick={handleSaveChatSelection}
            >
              Save Selection
            </button>
          </div>
        </div>
      )}
      {activeOverlay === 'saveStrategy' && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/40 backdrop-blur animate-fade-in">
          <div className="w-full sm:w-auto bg-zinc-900/95 border border-blue-500/20 rounded-2xl shadow-2xl p-6 min-w-[340px] max-w-[95vw] flex flex-col gap-4 relative animate-fade-in">
            <button className="absolute top-3 right-3 p-2 rounded-full hover:bg-zinc-800 transition" onClick={() => setActiveOverlay('none')}><X size={20} /></button>
            <div className="font-bold text-lg text-blue-300 mb-2">Save Strategy</div>
            <label className="font-semibold text-blue-200 mb-1">Name:</label>
            <input
              type="text"
              className="w-full bg-zinc-800 text-blue-300 rounded px-3 py-2 mb-2"
              value={newFilter.name || ''}
              onChange={e => setNewFilter(f => ({ ...f, name: e.target.value }))}
            />
            <button
              className="w-full py-2 rounded-lg bg-gradient-to-r from-blue-500 to-green-500 font-bold text-white shadow hover:scale-105 transition-all mt-2"
              onClick={handleSaveStrategy}
            >
              Save Strategy
            </button>
          </div>
        </div>
      )}
      {/* Filter Added Toast */}
      {filterAddedToast && (
        <div className="fixed bottom-8 right-8 z-50 bg-green-600/90 text-white px-4 py-2 rounded-xl shadow-lg font-semibold animate-fade-in">
          <span>Filter added:</span>
          <span className="ml-2 font-mono">{filterAddedToast}</span>
        </div>
      )}
    </section>
  );
}
