import React from "react";
import { useLocation, matchPath } from "react-router-dom";
import ColumnToggleBar from "./ColumnToggleBar";

export default function LayoutWithColumnBar({ children }) {
  const location = useLocation();
  // Show bar on /feed and /terminal/:tokenName
  const showBar = location.pathname === "/feed" || matchPath("/terminal/:tokenName", location.pathname);

  return (
    <div className="relative min-h-screen">
      {children}
      {showBar && <ColumnToggleBar />}
    </div>
  );
}
