import React, { useRef, useEffect, useState } from 'react';

// Generate random candle data for realism
function generateCandles(count = 8, min = 15, max = 40) {
  let candles = [];
  let lastClose = Math.floor((min + max) / 2);
  for (let i = 0; i < count; i++) {
    let open = lastClose;
    let close = open + Math.floor(Math.random() * 10 - 5);
    let high = Math.max(open, close) + Math.floor(Math.random() * 4);
    let low = Math.min(open, close) - Math.floor(Math.random() * 4);
    candles.push({
      x: 8 + i * 12,
      open,
      close,
      high,
      low,
      up: close >= open
    });
    lastClose = close;
  }
  return candles;
}

function MiniCandleChart() {
  const candles = React.useMemo(() => generateCandles(7, 15, 40), []);
  // Find min/max for grid lines
  const minY = Math.min(...candles.map(c => c.low));
  const maxY = Math.max(...candles.map(c => c.high));
  // Price line at last close
  const priceY = 48 - ((candles[candles.length - 1].close - minY) / (maxY - minY + 1e-6)) * 40 - 4;
  return (
    <svg width="100" height="48" viewBox="0 0 100 48" className="block mx-auto" style={{ display: 'block' }}>
      <rect x="0" y="0" width="100" height="48" rx="20" fill="#181A20" />
      {/* Grid lines */}
      {[1, 2, 3].map(i => (
        <line key={i} x1="8" x2="92" y1={8 + i * 10} y2={8 + i * 10} stroke="#23262F" strokeWidth="1" />
      ))}
      {/* Candles */}
      {candles.map((c, i) => {
        // Map price to y
        const y = v => 48 - ((v - minY) / (maxY - minY + 1e-6)) * 40 - 4;
        return (
          <g key={i}>
            {/* Wick */}
            <rect x={c.x + 4} y={y(c.high)} width={2} height={Math.max(2, y(c.low) - y(c.high))} fill={c.up ? '#16c784' : '#ea3943'} rx={2} />
            {/* Body */}
            <rect
              x={c.x}
              y={Math.min(y(c.open), y(c.close))}
              width={10}
              height={Math.max(4, Math.abs(y(c.open) - y(c.close)))}
              fill={c.up ? '#16c784' : '#ea3943'}
              rx={4}
              opacity={0.95}
            />
          </g>
        );
      })}
      {/* Price line */}
      <line x1="8" x2="92" y1={priceY} y2={priceY} stroke="#38bdf8" strokeDasharray="2 2" strokeWidth="1" opacity="0.7" />
    </svg>
  );
}

// Sleek, animated, interactive bonding curve
function AnimatedBondingCurve() {
  const [phase, setPhase] = useState(0);
  const [hovered, setHovered] = useState(false);
  const requestRef = useRef();

  useEffect(() => {
    if (!hovered) return;
    const animate = () => {
      setPhase(p => p + 0.08);
      requestRef.current = requestAnimationFrame(animate);
    };
    requestRef.current = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(requestRef.current);
  }, [hovered]);

  // Generate a smooth, undulating curve
  const points = Array.from({ length: 40 }, (_, i) => {
    const x = i * 2.3 + 5;
    // Sine wave for animation
    const y = 30 - 18 * Math.pow(i / 39, 1.7) + (hovered ? Math.sin(phase + i / 4) * 2 : 0);
    return `${x},${y}`;
  }).join(' ');

  return (
    <div
      className="transition-all duration-300 w-full flex justify-center items-center cursor-pointer"
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <svg width="100" height="36" viewBox="0 0 100 36" className="block mx-auto">
        <defs>
          <linearGradient id="bondingGradient" x1="0" y1="0" x2="100" y2="0" gradientUnits="userSpaceOnUse">
            <stop stopColor="#6366f1" />
            <stop offset="1" stopColor="#38bdf8" />
          </linearGradient>
          <filter id="glow" x="-20%" y="-20%" width="140%" height="140%">
            <feGaussianBlur stdDeviation="4" result="coloredBlur" />
            <feMerge>
              <feMergeNode in="coloredBlur" />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
        </defs>
        <rect x="0" y="0" width="100" height="36" rx="16" fill="#181A20" />
        <polyline
          points={points}
          fill="none"
          stroke="url(#bondingGradient)"
          strokeWidth="3.5"
          filter="url(#glow)"
          opacity="0.98"
        />
      </svg>
    </div>
  );
}

export default function FeedCard({
  icon,
  name,
  subtitle,
  roi,
  holders,
  dh,
  t10,
  mc,
  ath,
  vol,
  chart,
  onWatch,
  onDetails
}) {
  return (
    <div className="group relative bg-zinc-900/50 backdrop-blur-2xl rounded-[28px] border border-zinc-700/30 shadow-2xl p-7 flex flex-col gap-3 transition-all duration-300 cursor-pointer font-sans
      hover:scale-[1.03] hover:shadow-[0_0_32px_0_rgba(59,130,246,0.18)] hover:border-blue-400/40">
      <div className="flex items-center gap-4 mb-1">
        <div className="w-14 h-14 rounded-2xl bg-zinc-800 border border-zinc-700 flex items-center justify-center text-3xl overflow-hidden shadow-[0_2px_16px_0_rgba(59,130,246,0.10)]">
          {icon || <span className="text-zinc-500">?</span>}
        </div>
        <div className="flex-1 min-w-0">
          <div className="flex items-center gap-2">
            <span className="font-extrabold text-white text-xl truncate tracking-tight drop-shadow-[0_2px_8px_rgba(59,130,246,0.10)]">{name}</span>
            <span className="ml-2 px-2 py-0.5 rounded bg-gradient-to-r from-green-400/80 to-green-600/80 text-white text-xs font-bold shadow-lg animate-pulse">
              {roi}
            </span>
          </div>
          <div className="text-zinc-400 text-sm truncate font-medium">{subtitle}</div>
        </div>
        <button
          className="ml-2 p-2 rounded-full bg-zinc-800/80 hover:bg-blue-900/40 transition text-blue-400 shadow-md border border-zinc-700/40 hover:scale-110"
          title="Watch"
          onClick={e => { e.stopPropagation(); onWatch && onWatch(); }}
        >
          <svg width="22" height="22" fill="none" viewBox="0 0 24 24"><path d="M12 5c-7 0-9 7-9 7s2 7 9 7 9-7 9-7-2-7-9-7zm0 12a5 5 0 110-10 5 5 0 010 10z" stroke="currentColor" strokeWidth="2"/></svg>
        </button>
      </div>
      <div className="flex items-center gap-4 text-sm text-zinc-400 mb-1 font-semibold">
        <span>Holders: <span className="text-white font-bold">{holders}</span></span>
        <span>DH: <span className="text-white font-bold">{dh}</span></span>
        <span>T10: <span className="text-white font-bold">{t10}</span></span>
      </div>
      <div className="flex items-center gap-4 text-sm text-zinc-400 mb-1 font-semibold">
        <span>MC: <span className="text-white font-bold">{mc}</span></span>
        <span>ATH: <span className="text-white font-bold">{ath}</span></span>
        <span>Vol: <span className="text-white font-bold">{vol}</span></span>
      </div>
      {/* Mini SVG candlestick chart */}
      <div className="w-full flex justify-center my-1 rounded-2xl bg-zinc-800/60 p-2 shadow-inner">
        <MiniCandleChart />
      </div>
      {/* Sleek animated bonding curve */}
      <div className="w-full flex justify-center my-1 rounded-2xl bg-zinc-800/60 p-2 shadow-inner">
        <AnimatedBondingCurve />
      </div>
      <div className="w-full flex justify-center mb-1">
        <span className="text-xs text-blue-300/70 font-semibold tracking-wide">Bonding Curve</span>
      </div>
      <div className="flex gap-3 mt-2">
        <button
          className="flex-1 py-2 rounded-xl bg-zinc-900/80 text-zinc-200 hover:bg-blue-900/30 hover:text-blue-400 transition text-base font-bold shadow-md border border-zinc-700/40"
          onClick={e => { e.stopPropagation(); onDetails && onDetails(); }}
        >
          Details
        </button>
        <button
          className="flex-1 py-2 rounded-xl bg-gradient-to-r from-blue-500/80 to-green-400/80 text-white hover:from-blue-400 hover:to-green-300 transition text-base font-extrabold shadow-lg border border-blue-400/20 hover:scale-105"
          onClick={e => { e.stopPropagation(); onWatch && onWatch(); }}
        >
          + Watch
        </button>
      </div>
    </div>
  );
}
