import React from 'react';

const SOL_LOGO = (
  <svg width="28" height="28" viewBox="0 0 32 32" fill="none">
    <defs>
      <linearGradient id="sol1" x1="0" y1="0" x2="32" y2="32" gradientUnits="userSpaceOnUse">
        <stop stopColor="#9945FF" />
        <stop offset="0.5" stopColor="#14F195" />
        <stop offset="1" stopColor="#00FFA3" />
      </linearGradient>
    </defs>
    <rect width="32" height="32" rx="16" fill="#181A20" />
    <g>
      <rect x="7" y="9" width="18" height="3" rx="1.5" fill="url(#sol1)" />
      <rect x="7" y="15" width="18" height="3" rx="1.5" fill="url(#sol1)" opacity="0.7" />
      <rect x="7" y="21" width="18" height="3" rx="1.5" fill="url(#sol1)" opacity="0.5" />
    </g>
  </svg>
);

export default function QuickBuyBar({ amount, onAmountChange }) {
  return (
    <div className="flex items-center gap-3 bg-zinc-900/80 rounded-xl border border-zinc-800 px-4 py-2 shadow-lg backdrop-blur-md" style={{ width: 180 }}>
      <span className="text-zinc-100 font-semibold text-sm mr-2">quick buy</span>
      <div className="flex items-center gap-2 bg-zinc-800/80 rounded-lg px-2 py-1 border border-zinc-700">
        <span className="w-6 h-6 flex items-center justify-center">{SOL_LOGO}</span>
        <input
          type="number"
          min="0.0001"
          step="0.0001"
          value={amount}
          onChange={e => onAmountChange(e.target.value)}
          className="w-14 bg-transparent text-white text-base font-bold outline-none border-none text-right px-1"
        />
      </div>
    </div>
  );
}
