import React, { useState } from 'react';
import { useDashboard } from '../context/DashboardContext';
import StrategyOverlay from './StrategyOverlay';

function TradingViewWidget({ symbol }) {
  const [loading, setLoading] = useState(true);
  return (
    <div className="relative h-full">
      {loading && (
        <div className="absolute inset-0 flex items-center justify-center bg-black/40 z-10 animate-pulse">
          Loading chartâ€¦
        </div>
      )}
      <iframe
        title="TradingView Chart"
        src={`https://www.tradingview.com/widgetembed/?symbol=${symbol || 'SOLUSDT'}&interval=5&hidesidetoolbar=1&theme=dark&style=1&timezone=Etc/UTC`}
        style={{ width: '100%', height: '100%', border: 'none', borderRadius: 12 }}
        allowFullScreen
        onLoad={() => setLoading(false)}
      />
    </div>
  );
}

export default function ChartArea() {
  const { selectedToken, activeStrategy } = useDashboard();
  const symbolMap = { PAWPAW: 'SOLUSDT', 'BABY BOB': 'BTCUSDT', RUGPULL: 'ETHUSDT' };
  const symbol = selectedToken ? symbolMap[selectedToken.name] || 'SOLUSDT' : 'SOLUSDT';

  return (
    <div className="h-full flex flex-col">
      <div className="flex items-center justify-between mb-2">
        <h2 className="text-lg font-semibold">{symbol}</h2>
        <div className="flex items-center gap-2">
          <span className="text-sm text-gray-400">Strategy:</span>
          <span className="text-blue-400">{activeStrategy?.name || 'None'}</span>
        </div>
      </div>
      <div className="flex-1">
        <TradingViewWidget symbol={symbol} />
      </div>
      <StrategyOverlay />
    </div>
  );
}
