                              <option value="Day 1 Spike">Day 1 Spike</option>
                              <option value="Re-Trend">Re-Trend</option>
                              <option value="Trended Yesterday">Trended Yesterday</option>
                              <option value="Missed Trend">Missed Trend</option>
                              {/* Add more overlays as needed */}
                            </select>
                          </div>
                          <button onClick={() => setOpenFilter(null)} className="absolute top-4 right-4 p-2 rounded-full bg-zinc-800/60 hover:bg-blue-900/40 transition"><X size={22} /></button>
