                  ) : !filteredTokens[col.key] || filteredTokens[col.key].length === 0 ? (
                    <div className="flex flex-col items-center justify-center h-40 text-zinc-500 text-lg font-semibold gap-2">
                      <span>No tokens match your filters.</span>
                      <span className="text-sm text-zinc-400">Try adjusting your filter logic or meta overlay.</span>
                    </div>
