import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { DashboardProvider } from './context/DashboardContext';
import WelcomeScreen from './components/WelcomeScreen';
import TemplatesTab from './components/TemplatesTab';
import CreateTab from './components/CreateTab';
import DiscoverTab from './components/DiscoverTab';
import DashboardTab from './components/DashboardTab';
import BuilderTab from './components/BuilderTab';
import FeedPage from './pages/FeedPage';
import TradeTerminal from './components/TradeTerminal';
import StrategyWorkspace from './components/StrategyWorkspace';

const NAV = [
  { name: 'Feed', path: '/feed' },
  { name: 'FAQ', path: '/faq' },
  { name: 'Import', path: '/import' },
];

function App() {
  const [currentPage, setCurrentPage] = useState('welcome');
  const [showBuilder, setShowBuilder] = useState(false);
  const [selectedTemplate, setSelectedTemplate] = useState(null);
  const [newStrategyName, setNewStrategyName] = useState(null);

  const handleTryFlow = (template) => {
    setSelectedTemplate(template);
    setShowBuilder(true);
    setCurrentPage('builder');
  };

  const handleAddToFeed = (strategyName) => {
    setNewStrategyName(strategyName);
    setCurrentPage('trade');
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'welcome':
        return <WelcomeScreen onNavigate={setCurrentPage} />;
      case 'templates':
        return <TemplatesTab onTryFlow={handleTryFlow} />;
      case 'create':
        return <CreateTab />;
      case 'discover':
        return <DiscoverTab onAddToFeed={handleAddToFeed} />;
      case 'dashboard':
        return <DashboardTab />;
      case 'feed':
        return <FeedPage />;
      case 'trade':
        return <TradeTerminal newStrategyName={newStrategyName} />;
      case 'workspace':
        return <StrategyWorkspace />;
      case 'builder':
        return showBuilder && <BuilderTab template={selectedTemplate} />;
      default:
        return <WelcomeScreen onNavigate={setCurrentPage} />;
    }
  };

  return (
    <DashboardProvider>
      <DndProvider backend={HTML5Backend}>
        <div className="min-h-screen bg-zinc-900 text-white">
          {/* Top Navigation Bar */}
          <div className="flex justify-between items-center px-6 py-4 bg-black/80 backdrop-blur-md shadow-sm border-b border-neutral-800 sticky top-0 z-10">
            <div className="flex items-center space-x-4">
              <h1 
                className="text-xl font-bold text-white cursor-pointer" 
                onClick={() => setCurrentPage('welcome')}
              >
                SCOPE<sup className="text-white">2</sup>
              </h1>
              <div className="flex space-x-4 text-sm text-white/80">
                {NAV.map((item) => (
                  <button
                    key={item.name}
                    onClick={() => setCurrentPage(item.name.toLowerCase())}
                    className="font-semibold px-4 py-2 rounded-full transition-all duration-200 shadow-inner hover:text-white/90"
                  >
                    {item.name}
                  </button>
                ))}
              </div>
            </div>
            <div className="text-sm text-right text-purple-400 italic drop-shadow-[0_0_6px_rgba(168,85,247,0.7)]">
              <span className="font-semibold text-purple-500">news2pump</span> powered
            </div>
          </div>

          <main className="flex flex-col items-center justify-center flex-1 w-full">
            {renderPage()}
          </main>
        </div>
      </DndProvider>
    </DashboardProvider>
  );
}

export default App;
