import React from "react";
import { useColumnVisibility } from "../context/ColumnVisibilityContext";

export default function ColumnToggleBar() {
  const { columns, visibleColumns, toggleColumn } = useColumnVisibility();

  return (
    <div className="fixed bottom-0 left-0 w-full z-50 flex justify-center bg-black/70 backdrop-blur border-t border-zinc-800 shadow-lg py-2 px-4 gap-2">
      {columns.map(col => (
        <button
          key={col.id}
          className={`px-4 py-2 rounded-xl font-bold transition-all
            ${visibleColumns.includes(col.id)
              ? "bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-md"
              : "bg-zinc-900/70 text-zinc-400 border border-zinc-700"}
            hover:scale-105`}
          onClick={() => toggleColumn(col.id)}
        >
          {col.name}
        </button>
      ))}
    </div>
  );
}
