import React from 'react';
import { GlossCard } from './GlossCard';

const icons = {
  templates: (
    <svg width="40" height="40" fill="none" viewBox="0 0 24 24"><rect x="3" y="5" width="18" height="14" rx="3" stroke="currentColor" strokeWidth="2"/><rect x="7" y="9" width="10" height="2" rx="1" fill="currentColor"/><rect x="7" y="13" width="6" height="2" rx="1" fill="currentColor"/></svg>
  ),
  create: (
    <svg width="48" height="48" fill="none" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/><path d="M12 8v8M8 12h8" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/></svg>
  ),
  discover: (
    <svg width="40" height="40" fill="none" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/><path d="M12 8v4l3 3" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/></svg>
  ),
};

const cards = [
  { title: 'Templates', desc: 'Explore prebuilt workflows', color: 'blue-400', icon: icons.templates },
  { title: 'Create', desc: 'Build your own workspace', color: 'green-400', icon: icons.create },
  { title: 'Discover', desc: 'Browse workflows from the community', color: 'purple-400', icon: icons.discover },
];

export default function WelcomeScreen({ onCardClick }) {
  return (
    <div className="min-h-[80vh] flex flex-col items-center justify-center px-4 py-12">
      <div className="text-center mb-12">
        <h1
          className="text-7xl font-extrabold tracking-widest mb-4 bg-gradient-to-r from-gray-100 via-gray-400 to-gray-100 bg-clip-text text-transparent"
          style={{
            textShadow:
              '0 6px 32px #000, 0 2px 0 #fff2, 0 1px 0 #fff4, 0 0.5px 0 #fff6',
            letterSpacing: '0.15em',
          }}
        >
          SCOPE<sup className="text-3xl align-super ml-1">Â²</sup>
        </h1>
        <p className="text-2xl text-gray-300 drop-shadow-[0_1px_4px_rgba(0,0,0,0.7)] font-medium">
          Choose a trading workflow to get started
        </p>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-10 max-w-5xl w-full">
        {cards.map(card => (
          <button
            key={card.title}
            className="focus:outline-none group rounded-2xl transition hover:scale-105 hover:shadow-lg"
            onClick={() => onCardClick && onCardClick(card.title)}
            type="button"
          >
            <GlossCard {...card} />
          </button>
        ))}
      </div>
    </div>
  );
}
