import React, { useState } from 'react';
import { useDashboard } from '../context/DashboardContext';

export default function TradeTerminal() {
  const { selectedToken } = useDashboard();
  const [amount, setAmount] = useState('0.01 SOL');
  const [orderType, setOrderType] = useState('Market');
  return (
    <div className="card-wrapper p-6">
      <h2 className="text-lg font-semibold mb-4">Trading Terminal</h2>
      {selectedToken ? (
        <>
          <div className="text-white mb-2">Token: ${selectedToken.name}</div>
          <div className="text-sm text-white/60 mb-4">
            Price: {selectedToken.price} | Cap: {selectedToken.cap} | Strategy: {selectedToken.strategy}
          </div>
          <div className="mt-4">
            <label className="block text-sm mb-1">Amount</label>
            <select
              className="bg-black border border-white/10 rounded-md px-2 py-1 text-white w-full mb-4"
              value={amount}
              onChange={e => setAmount(e.target.value)}
            >
              <option>0.01 SOL</option>
              <option>0.1 SOL</option>
              <option>1 SOL</option>
            </select>
            <div className="flex gap-2 mb-2">
              <button
                className={`flex-1 py-1 rounded-md text-sm font-semibold border ${orderType === 'Market' ? 'bg-green-600 text-white border-green-700' : 'bg-black text-white/60 border-white/10'} transition`}
                onClick={() => setOrderType('Market')}
              >Market</button>
              <button
                className={`flex-1 py-1 rounded-md text-sm font-semibold border ${orderType === 'Limit' ? 'bg-blue-600 text-white border-blue-700' : 'bg-black text-white/60 border-white/10'} transition`}
                onClick={() => setOrderType('Limit')}
              >Limit</button>
            </div>
            <div className="text-xs text-white/50 mt-2">Slippage: 0.5%</div>
          </div>
          <div className="space-y-3 mt-4">
            <button className="w-full py-2 rounded-md bg-green-600 hover:bg-green-500 transition">Buy Chill</button>
            <button className="w-full py-2 rounded-md bg-red-600 hover:bg-red-500 transition">Sell Fast</button>
          </div>
          <div className="mt-4 text-xs text-green-500">Tx Status: Ready</div>
          <div className="flex justify-between mt-3">
            <button className="bg-gray-800 text-sm px-3 py-1 rounded">Preset 1</button>
            <button className="bg-gray-800 text-sm px-3 py-1 rounded">Preset 2</button>
          </div>
        </>
      ) : (
        <div className="text-white/40">Select a token to trade</div>
      )}
    </div>
  );
}
