import React, { useState } from 'react';

export default function StrategyWorkspace({ strategy, goBack }) {
  const [selectedToken, setSelectedToken] = useState(null);
  const [timeframe, setTimeframe] = useState('1h');
  const [chartType, setChartType] = useState('candles');

  // Mock data for associated tokens
  const associatedTokens = [
    {
      symbol: 'BTC',
      name: 'Bitcoin',
      price: 45123.45,
      change24h: 2.5,
      volume: 2500000000,
      marketCap: 850000000000,
      strategyWeight: 0.4
    },
    {
      symbol: 'ETH',
      name: 'Ethereum',
      price: 2345.67,
      change24h: 1.8,
      volume: 1500000000,
      marketCap: 280000000000,
      strategyWeight: 0.3
    },
    {
      symbol: 'SOL',
      name: 'Solana',
      price: 98.45,
      change24h: 5.2,
      volume: 800000000,
      marketCap: 42000000000,
      strategyWeight: 0.2
    },
    {
      symbol: 'AVAX',
      name: 'Avalanche',
      price: 34.56,
      change24h: 3.1,
      volume: 400000000,
      marketCap: 12000000000,
      strategyWeight: 0.1
    }
  ];

  // Mock candlestick data for the placeholder chart
  const generateMockCandles = () => {
    const candles = [];
    const basePrice = selectedToken?.price || 45000;
    for (let i = 0; i < 50; i++) {
      const open = basePrice * (1 + (Math.random() - 0.5) * 0.02);
      const close = open * (1 + (Math.random() - 0.5) * 0.02);
      const high = Math.max(open, close) * (1 + Math.random() * 0.01);
      const low = Math.min(open, close) * (1 - Math.random() * 0.01);
      candles.push({ open, high, low, close });
    }
    return candles;
  };

  const mockCandles = generateMockCandles();

  return (
    <div className="w-full h-screen bg-zinc-900 text-white p-4">
      {/* Header */}
      <div className="flex justify-between items-center mb-4">
        <div className="flex items-center gap-4">
          <button
            onClick={goBack}
            className="text-blue-400 hover:text-blue-300"
          >
            ← Back to Discover
          </button>
          <h2 className="text-2xl font-bold">{strategy.title}</h2>
        </div>
        <div className="flex items-center gap-4">
          <div className="text-sm text-gray-400">Strategy by @{strategy.user}</div>
          <div className="flex gap-2">
            <button 
              className={`px-3 py-1 rounded ${timeframe === '1h' ? 'bg-blue-500' : 'bg-zinc-800'}`}
              onClick={() => setTimeframe('1h')}
            >
              1h
            </button>
            <button 
              className={`px-3 py-1 rounded ${timeframe === '4h' ? 'bg-blue-500' : 'bg-zinc-800'}`}
              onClick={() => setTimeframe('4h')}
            >
              4h
            </button>
            <button 
              className={`px-3 py-1 rounded ${timeframe === '1d' ? 'bg-blue-500' : 'bg-zinc-800'}`}
              onClick={() => setTimeframe('1d')}
            >
              1d
            </button>
            <button 
              className={`px-3 py-1 rounded ${timeframe === '1w' ? 'bg-blue-500' : 'bg-zinc-800'}`}
              onClick={() => setTimeframe('1w')}
            >
              1w
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="grid grid-cols-12 gap-4 h-[calc(100vh-8rem)]">
        {/* Left Panel - Token List */}
        <div className="col-span-3 flex flex-col gap-4">
          {/* Token List */}
          <div className="bg-zinc-800 rounded-lg p-4 flex-1 overflow-y-auto">
            <h3 className="text-lg font-semibold mb-4">Strategy Tokens</h3>
            <div className="space-y-2">
              {associatedTokens.map((token) => (
                <div
                  key={token.symbol}
                  className={`p-3 rounded-lg cursor-pointer transition-all ${
                    selectedToken?.symbol === token.symbol
                      ? 'bg-blue-500/20 border border-blue-500'
                      : 'bg-zinc-700/50 hover:bg-zinc-700'
                  }`}
                  onClick={() => setSelectedToken(token)}
                >
                  <div className="flex justify-between items-center mb-1">
                    <div className="font-semibold">{token.symbol}</div>
                    <div className={`text-sm ${token.change24h >= 0 ? 'text-green-500' : 'text-red-500'}`}>
                      {token.change24h}%
                    </div>
                  </div>
                  <div className="text-sm text-gray-400">{token.name}</div>
                  <div className="text-sm text-gray-400">Weight: {token.strategyWeight * 100}%</div>
                  <div className="text-sm text-gray-400">Price: ${token.price.toLocaleString()}</div>
                </div>
              ))}
            </div>
          </div>

          {/* Strategy Info */}
          <div className="bg-zinc-800 rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-4">Strategy Info</h3>
            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-400">Performance (30d)</span>
                <span className="text-green-500">{strategy.performance}%</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">Risk Level</span>
                <span className="text-yellow-500">{strategy.risk}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">Followers</span>
                <span>{strategy.followers.toLocaleString()}</span>
              </div>
            </div>
          </div>
        </div>

        {/* Right Panel - Chart */}
        <div className="col-span-9 flex flex-col gap-4">
          {/* Chart Controls */}
          <div className="bg-zinc-800 rounded-lg p-4">
            <div className="flex justify-between items-center">
              <div>
                <h3 className="text-xl font-bold">
                  {selectedToken ? `${selectedToken.symbol}/USDT` : 'Select a token'}
                </h3>
                {selectedToken && (
                  <div className="text-sm text-gray-400">
                    ${selectedToken.price.toLocaleString()} • {selectedToken.change24h}% (24h)
                  </div>
                )}
              </div>
              <div className="flex gap-2">
                <button 
                  className={`px-3 py-1 rounded ${chartType === 'candles' ? 'bg-blue-500' : 'bg-zinc-700'}`}
                  onClick={() => setChartType('candles')}
                >
                  Candles
                </button>
                <button 
                  className={`px-3 py-1 rounded ${chartType === 'line' ? 'bg-blue-500' : 'bg-zinc-700'}`}
                  onClick={() => setChartType('line')}
                >
                  Line
                </button>
                <button 
                  className={`px-3 py-1 rounded ${chartType === 'depth' ? 'bg-blue-500' : 'bg-zinc-700'}`}
                  onClick={() => setChartType('depth')}
                >
                  Depth
                </button>
              </div>
            </div>
          </div>

          {/* Chart Placeholder */}
          <div className="bg-zinc-800 rounded-lg p-4 flex-1">
            {selectedToken ? (
              <div className="w-full h-full flex flex-col">
                {/* Chart Toolbar */}
                <div className="flex gap-2 mb-4">
                  <div className="flex gap-1">
                    <button className="px-2 py-1 bg-zinc-700 rounded text-xs">RSI</button>
                    <button className="px-2 py-1 bg-zinc-700 rounded text-xs">MACD</button>
                    <button className="px-2 py-1 bg-zinc-700 rounded text-xs">MA</button>
                  </div>
                  <div className="flex gap-1 ml-auto">
                    <button className="px-2 py-1 bg-zinc-700 rounded text-xs">Fullscreen</button>
                    <button className="px-2 py-1 bg-zinc-700 rounded text-xs">Settings</button>
                  </div>
                </div>

                {/* Chart Area */}
                <div className="flex-1 bg-black rounded-lg relative overflow-hidden">
                  {/* Price Scale */}
                  <div className="absolute left-0 top-0 bottom-0 w-12 border-r border-zinc-700 flex flex-col justify-between p-2">
                    {[100, 75, 50, 25, 0].map((percent) => (
                      <div key={percent} className="text-xs text-gray-500">
                        ${(selectedToken.price * (1 + (50 - percent) / 100)).toLocaleString()}
                      </div>
                    ))}
                  </div>

                  {/* Time Scale */}
                  <div className="absolute bottom-0 left-12 right-0 h-8 border-t border-zinc-700 flex justify-between px-2">
                    {['9:00', '12:00', '15:00', '18:00', '21:00'].map((time) => (
                      <div key={time} className="text-xs text-gray-500">{time}</div>
                    ))}
                  </div>

                  {/* Candlesticks */}
                  <div className="absolute left-12 right-0 top-0 bottom-8 p-4">
                    <div className="w-full h-full relative">
                      {mockCandles.map((candle, index) => {
                        const isUp = candle.close >= candle.open;
                        const height = 100 * (candle.high - candle.low) / (selectedToken.price * 0.1);
                        const bodyHeight = 100 * Math.abs(candle.close - candle.open) / (selectedToken.price * 0.1);
                        const y = 100 * (candle.high - selectedToken.price) / (selectedToken.price * 0.1);
                        
                        return (
                          <div
                            key={index}
                            className="absolute bottom-0"
                            style={{
                              left: `${(index / mockCandles.length) * 100}%`,
                              width: `${100 / mockCandles.length}%`,
                              height: `${height}%`,
                              transform: `translateY(${y}%)`
                            }}
                          >
                            <div
                              className={`absolute w-full ${
                                isUp ? 'bg-green-500' : 'bg-red-500'
                              }`}
                              style={{
                                height: `${bodyHeight}%`,
                                top: isUp ? '0' : 'auto',
                                bottom: isUp ? 'auto' : '0'
                              }}
                            />
                            <div
                              className={`absolute w-px ${
                                isUp ? 'bg-green-500' : 'bg-red-500'
                              }`}
                              style={{
                                height: '100%',
                                left: '50%',
                                transform: 'translateX(-50%)'
                              }}
                            />
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              <div className="h-full flex items-center justify-center text-gray-500">
                Select a token to view its chart
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
