import React, { useState, useEffect, useRef } from 'react';
import FeedCard from './FeedCard';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Sliders, Settings, Check, X } from 'lucide-react';

const FEED_COLUMNS = [
  { key: 'new', label: 'newly created' },
  { key: 'graduating', label: 'about to graduate' },
  { key: 'graduated', label: 'graduated' },
  { key: 'featured', label: 'featured' },
];

const FILTER_CATEGORIES = [
  'Volume',
  'Market Cap',
  'Smart Wallet Behavior',
  'Holder Growth',
  'Token Age',
  'Retracement / Price Action',
  'Scam Filter Confidence',
  'Social Signals',
  'Meta Lifecycle',
  'Platform Origin',
  'Watchlist Momentum',
  'Verified Wallet Activity',
  'Call Source Origin',
];

// Mock data for each column
const MOCK_FEEDS = {
  new: [
    {
      icon: 'ðŸ§¢', name: 'FEDORACOIN', subtitle: 'The First Memecoin', roi: '+28%', holders: 0, dh: '0%', t10: '0%', mc: '$4.1K', ath: '$4.1K', vol: '$4.1K',
    },
    {
      icon: 'ðŸ“±', name: 'MOBILE', subtitle: 'Motorola DynaTAC 8000X', roi: '+13%', holders: 0, dh: '0%', t10: '0%', mc: '$4.1K', ath: '$4.1K', vol: '$4.1K',
    },
    {
      icon: 'ðŸ’©', name: 'SOTUS', subtitle: 'Shitter Of The United States', roi: '+14%', holders: 0, dh: '0%', t10: '0%', mc: '$4.1K', ath: '$4.1K', vol: '$4.1K',
    },
  ],
  graduating: [
    {
      icon: 'ðŸ³ï¸â€ðŸŒˆ', name: 'GAY', subtitle: 'Gay Nigga Season', roi: '-4%', holders: 35, dh: '10%', t10: '19%', mc: '$41.6K', ath: '$44.5K', vol: '$6.4K',
    },
    {
      icon: 'ðŸ‡ºðŸ‡¸', name: 'USCR', subtitle: 'U.S. Crypto Reserve Index', roi: '+3%', holders: 27, dh: '72%', t10: '73%', mc: '$42.8K', ath: '$42.7K', vol: '$12.4K',
    },
    {
      icon: 'ðŸ§‘â€âš–ï¸', name: 'pope fong', subtitle: 'pope fong', roi: '+116%', holders: 84, dh: '4%', t10: '26%', mc: '$41.6K', ath: '$41.6K', vol: '$10.1K',
    },
  ],
  graduated: [
    {
      icon: 'ðŸŸ§', name: 'FEDORA', subtitle: 'The First Memecoin', roi: '+2.2K%', holders: 309, dh: '40%', t10: '10%', mc: '$60.2K', ath: '$60.2K', vol: '$161.5K',
    },
    {
      icon: 'ðŸ§‘â€ðŸŽ¨', name: 'stickman', subtitle: 'stickman', roi: '+49%', holders: 122, dh: '26%', t10: '26%', mc: '$28.4K', ath: '$32.4K', vol: '$49.4K',
    },
    {
      icon: 'ðŸ§ ', name: 'GENIUS', subtitle: 'Genius AI', roi: '+4.7%', holders: 6, dh: '3%', t10: '3%', mc: '$2.4K', ath: '$2.4K', vol: '$2.4K',
    },
  ],
  featured: [
    {
      icon: 'ðŸ±', name: 'CKV', subtitle: 'Crypto KittyVerse', roi: '+10%', holders: 2, dh: '7%', t10: '5%', mc: '$4.5K', ath: '$5.5K', vol: '$2.0K',
    },
    {
      icon: 'ðŸ¦¸â€â™‚ï¸', name: 'SUS', subtitle: 'Super Ultra Sendor', roi: '+513%', holders: 5, dh: '-9%', t10: '22%', mc: '$14.4K', ath: '$27.2K', vol: '$58.3K',
    },
    {
      icon: 'ðŸŒ¸', name: 'MURAKAMI', subtitle: 'MURAKAMI', roi: '+3%', holders: 9, dh: '0.39%', t10: '7%', mc: '$4.3K', ath: '$4.7K', vol: '$419',
    },
  ],
};

export default function FeedGrid({ buyAmount }) {
  const [columns, setColumns] = useState(() => {
    const saved = localStorage.getItem('feedColumnOrder');
    return saved ? JSON.parse(saved) : FEED_COLUMNS;
  });
  const [openFilter, setOpenFilter] = useState(null); // Track which column's filter menu is open
  const filterMenuRef = useRef();
  const [selectedCategory, setSelectedCategory] = useState(FILTER_CATEGORIES[0]);
  const [filterConfigModal, setFilterConfigModal] = useState({ open: false, colKey: null, category: null });
  const [columnConfigs, setColumnConfigs] = useState({}); // { [colKey]: { category, configFields } }

  useEffect(() => {
    localStorage.setItem('feedColumnOrder', JSON.stringify(columns));
    console.log('Columns state updated:', columns);
    if (!Array.isArray(columns) || columns.length < 2) {
      console.warn('Columns array is not as expected:', columns);
    }
  }, [columns]);

  // Close filter menu on outside click
  useEffect(() => {
    function handleClick(e) {
      if (filterMenuRef.current && !filterMenuRef.current.contains(e.target)) {
        setOpenFilter(null);
      }
    }
    if (openFilter !== null) {
      document.addEventListener('mousedown', handleClick);
    }
    return () => document.removeEventListener('mousedown', handleClick);
  }, [openFilter]);

  function onDragEnd(result) {
    console.log('Drag end event:', result);
    if (!result.destination) return;
    const reordered = Array.from(columns);
    const [removed] = reordered.splice(result.source.index, 1);
    reordered.splice(result.destination.index, 0, removed);
    setColumns(reordered);
    // Move columnConfigs to match new column order
    setColumnConfigs(prev => {
      const newConfigs = {};
      reordered.forEach(col => {
        if (prev[col.key]) newConfigs[col.key] = prev[col.key];
      });
      return newConfigs;
    });
  }

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <Droppable droppableId="feed-columns" direction="horizontal">
        {(provided) => (
          <div
            className="flex gap-6 max-w-full"
            ref={provided.innerRef}
            {...provided.droppableProps}
            style={{ minWidth: 1200, overflow: 'visible', background: '#222' }}
          >
            {columns.map((col, idx) => (
              <Draggable key={col.key} draggableId={col.key} index={idx}>
                {(provided, snapshot) => (
                  <section
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    className={`flex flex-col bg-zinc-900/70 rounded-2xl border border-zinc-800 shadow-xl backdrop-blur-xl min-h-[80vh] transition-all duration-300 ${snapshot.isDragging ? 'ring-4 ring-blue-400/30 scale-105 z-20' : ''}`}
                  >
                    {/* Sticky Header as Drag Handle */}
                    <header
                      className="sticky top-0 z-10 bg-zinc-900/80 backdrop-blur border-b border-zinc-800 rounded-t-2xl flex items-center justify-between px-4 py-3 select-none cursor-grab active:cursor-grabbing"
                      {...provided.dragHandleProps}
                    >
                      <div className="flex items-center gap-2">
                        <span className="text-green-400 text-lg font-bold">â€¢</span>
                        <span className="text-lg font-semibold text-white capitalize">{col.label}</span>
                        <div className="relative">
                          <button
                            className="ml-2 text-xs text-zinc-400 hover:text-green-400 transition"
                            onClick={e => {
                              e.stopPropagation();
                              setOpenFilter(openFilter === col.key ? null : col.key);
                            }}
                          >
                            <Sliders size={18} />
                          </button>
                          {openFilter === col.key && (
                            <div
                              ref={filterMenuRef}
                              className="absolute right-0 mt-5 z-50 min-w-[250px] bg-black/80 border border-zinc-700 rounded-2xl shadow-2xl py-3 px-3 text-base text-white backdrop-blur-xl"
                              style={{ boxShadow: '0 8px 32px 0 rgba(30,30,60,0.35)' }}
                            >
                              <div className="font-bold text-zinc-200 px-3 pb-2 text-[17px] tracking-wide">Change Category</div>
                              <div className="h-1 w-full bg-gradient-to-r from-blue-400/30 via-green-400/20 to-transparent rounded-full mb-3" />
                              <div className="flex flex-col gap-2 max-h-72 overflow-y-auto pr-1 mb-2">
                                {FILTER_CATEGORIES.map(cat => (
                                  <button
                                    key={cat}
                                    className={`flex items-center gap-2 px-4 py-2 rounded-full bg-zinc-800/70 hover:bg-blue-900/40 transition font-semibold text-[15px] focus:outline-none shadow-sm hover:shadow-blue-400/10 ${selectedCategory === cat ? 'ring-2 ring-blue-400' : ''}`}
                                    onClick={() => {
                                      setOpenFilter(null);
                                      setSelectedCategory(cat);
                                      setFilterConfigModal({ open: true, colKey: col.key, category: cat });
                                    }}
                                  >
                                    <Sliders size={16} className="text-blue-400/70" />
                                    <span className="flex-1 text-left">{cat}</span>
                                    {selectedCategory === cat && <Check size={18} className="ml-auto text-blue-400" />}
                                  </button>
                                ))}
                              </div>
                              {/* Filter Config Modal */}
                              {filterConfigModal.open && filterConfigModal.colKey === col.key && (
                                <div className="absolute right-0 mt-3 z-50 min-w-[320px] bg-zinc-900/95 border border-blue-500/20 rounded-2xl shadow-2xl py-5 px-6 text-base text-white backdrop-blur-xl flex flex-col gap-4" style={{ top: '100%', marginTop: 12 }}>
                                  <div className="flex items-center justify-between mb-2">
                                    <div className="font-bold text-lg text-blue-300">Configure {filterConfigModal.category} Filter</div>
                                    <button onClick={() => setFilterConfigModal({ open: false, colKey: null, category: null })} className="p-1 rounded-full hover:bg-zinc-800 transition"><X size={20} /></button>
                                  </div>
                                  {/* Render fields based on category */}
                                  {filterConfigModal.category === 'Smart Wallet Behavior' && (
                                    <>
                                      <label className="flex flex-col gap-1">Wallet Win Rate (%)<input type="number" className="bg-zinc-800 rounded px-3 py-2 mt-1" placeholder="e.g. 60" /></label>
                                      <label className="flex flex-col gap-1">Buy Window (min)<input type="number" className="bg-zinc-800 rounded px-3 py-2 mt-1" placeholder="e.g. 10" /></label>
                                      <label className="flex flex-col gap-1">Min Wallet Count<input type="number" className="bg-zinc-800 rounded px-3 py-2 mt-1" placeholder="e.g. 3" /></label>
                                      <label className="flex flex-col gap-1">Token Age (days)<input type="number" className="bg-zinc-800 rounded px-3 py-2 mt-1" placeholder="e.g. 2" /></label>
                                    </>
                                  )}
                                  {filterConfigModal.category === 'Volume' && (
                                    <>
                                      <label className="flex flex-col gap-1">Min Volume<input type="number" className="bg-zinc-800 rounded px-3 py-2 mt-1" placeholder="e.g. 20000" /></label>
                                      <label className="flex flex-col gap-1">Time Window (min)<input type="number" className="bg-zinc-800 rounded px-3 py-2 mt-1" placeholder="e.g. 5" /></label>
                                      <label className="flex flex-col gap-1">Change %<input type="number" className="bg-zinc-800 rounded px-3 py-2 mt-1" placeholder="e.g. 10" /></label>
                                      <label className="flex flex-col gap-1">Slope<input type="number" className="bg-zinc-800 rounded px-3 py-2 mt-1" placeholder="e.g. 1.5" /></label>
                                    </>
                                  )}
                                  {/* Add more category configs as needed */}
                                  <button className="mt-2 py-2 rounded-lg bg-gradient-to-r from-blue-500 to-green-500 font-bold text-white shadow hover:scale-105 transition-all" onClick={() => setFilterConfigModal({ open: false, colKey: null, category: null })}>Save Filter</button>
                                </div>
                              )}
                            </div>
                          )}
                        </div>
                      </div>
                      <button className="ml-2 p-1 rounded-full bg-zinc-800 hover:bg-green-900/30 transition">
                        <div className="relative">
                          <button
                            className="p-1 rounded-full bg-zinc-800 hover:bg-green-900/30 transition"
                            onClick={e => {
                              e.stopPropagation();
                              setOpenFilter(openFilter === col.key + '-settings' ? null : col.key + '-settings');
                            }}
                          >
                            <Settings size={20} className="text-green-400" />
                          </button>
                          {openFilter === col.key + '-settings' && (
                            <div
                              ref={filterMenuRef}
                              className="absolute right-0 mt-3 z-50 min-w-[250px] bg-black/80 border border-zinc-700 rounded-2xl shadow-2xl py-3 px-3 text-base text-white backdrop-blur-xl"
                              style={{ boxShadow: '0 8px 32px 0 rgba(30,30,60,0.35)' }}
                            >
                              <div className="font-bold text-zinc-200 px-3 pb-2 text-[17px] tracking-wide">Change Category</div>
                              <div className="h-1 w-full bg-gradient-to-r from-blue-400/30 via-green-400/20 to-transparent rounded-full mb-3" />
                              <div className="flex flex-col gap-2 max-h-72 overflow-y-auto pr-1 mb-2">
                                {FILTER_CATEGORIES.map(cat => (
                                  <button
                                    key={cat}
                                    className={`flex items-center gap-2 px-4 py-2 rounded-full bg-zinc-800/70 hover:bg-blue-900/40 transition font-semibold text-[15px] focus:outline-none shadow-sm hover:shadow-blue-400/10 ${selectedCategory === cat ? 'ring-2 ring-blue-400' : ''}`}
                                    onClick={() => { setOpenFilter(null); setSelectedCategory(cat); }}
                                  >
                                    <Sliders size={16} className="text-blue-400/70" />
                                    <span className="flex-1 text-left">{cat}</span>
                                    {selectedCategory === cat && <Check size={18} className="ml-auto text-blue-400" />}
                                  </button>
                                ))}
                              </div>
                            </div>
                          )}
                        </div>
                      </button>
                    </header>
                    {/* Cards */}
                    <div className="flex-1 p-4 space-y-4">
                      {MOCK_FEEDS[col.key].map((item, idx) => (
                        <FeedCard key={item.name + idx} {...item} buyAmount={buyAmount} />
                      ))}
                    </div>
                  </section>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </DragDropContext>
  );
}
