  // Add more as needed
};

function CryptoTicker() {
  const [offset, setOffset] = useState(0);
  const tickerRef = useRef(null);
  const ITEM_WIDTH = 260; // px, slightly wider for more space
  const TICKER_SPEED = 40; // px per second
  const TICKER_REPEAT = 3; // repeat content to fill bar

  useEffect(() => {
    const interval = setInterval(() => {
      setOffset(prev => {
        const maxOffset = ITEM_WIDTH * TICKER_DATA.length * TICKER_REPEAT;
        const next = prev + 2;
        return next >= maxOffset ? 0 : next;
      });
    }, 1000 / 30); // 30 FPS
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative w-full overflow-hidden">
      {/* Gradient fade left/right */}
      <div className="pointer-events-none absolute left-0 top-0 h-full w-16 bg-gradient-to-r from-black/80 to-transparent z-10" />
      <div className="pointer-events-none absolute right-0 top-0 h-full w-16 bg-gradient-to-l from-black/80 to-transparent z-10" />
      <div
        className="flex items-center gap-8 transition-transform duration-200"
        style={{ transform: `translateX(-${offset}px)` }}
        ref={tickerRef}
      >
        {Array(TICKER_REPEAT).fill(0).flatMap((_, rep) =>
          TICKER_DATA.map((item, idx) => (
            <div
              key={rep + '-' + idx}
              className="min-w-[260px] px-8 py-2 rounded-xl bg-black/60 backdrop-blur-md shadow border border-zinc-800 flex items-center gap-4 mx-2"
            >
              <span className="font-extrabold text-lg text-white drop-shadow">{item.symbol}</span>
              <span className={`font-bold text-xl ${item.color}`}>{item.price}</span>
              <span className={`ml-2 font-semibold ${item.color} text-sm`}>{item.change}</span>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default function TradeTerminal({ newStrategyName }) {
  const { tokenName } = useParams();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('spot');
  const [orderType, setOrderType] = useState('limit');
  const [side, setSide] = useState('buy');
  const { activeStrategy } = useDashboard();
  const [buyAmount, setBuyAmount] = useState('0.01');

  // Real-time feed simulation: add a new trade every 10s
  const FEED_SIZE = 12;
  const [feed, setFeed] = useState(() => {
    // Start with the first FEED_SIZE trades
    return liveTrades.slice(0, FEED_SIZE).map((trade, i) => ({ ...trade, time: `${i * 10}s` }));
  });
  const [nextIndex, setNextIndex] = useState(FEED_SIZE);

  useEffect(() => {
    const interval = setInterval(() => {
      setFeed(prevFeed => {
        // Insert new trade at the top, shift times
        const newTrade = liveTrades[nextIndex % liveTrades.length];
        const updatedFeed = [
          { ...newTrade, time: '0s' },
          ...prevFeed.slice(0, FEED_SIZE - 1).map((t, i) => ({ ...t, time: `${(i + 1) * 10}s` }))
        ];
        return updatedFeed;
      });
      setNextIndex(prev => (prev + 1) % liveTrades.length);
    }, 10000); // 10 seconds
    return () => clearInterval(interval);
  }, [nextIndex]);

  // Start with no tabs; only add when Discover or preset is chosen
  const [strategyTabs, setStrategyTabs] = useState([]);
  const [activeStrategyTab, setActiveStrategyTab] = useState("");
  const [discoverTab, setDiscoverTab] = useState("");
  const addedRef = useRef(false);
  const [showPresetMenu, setShowPresetMenu] = useState(false);

  // Handle adding a new strategy tab
  const handleAddTab = () => {
    setShowPresetMenu(true);
  };

  // Handle removing a strategy tab
  const handleRemoveTab = (tab) => {
    setStrategyTabs(prev => prev.filter(t => t !== tab));
    if (activeStrategyTab === tab) {
      setActiveStrategyTab(strategyTabs[0] || "");
    }
  };

  // Handle selecting a preset strategy
  const handleSelectPreset = (preset) => {
    setStrategyTabs(prev => [...prev, preset]);
    setActiveStrategyTab(preset);
    setShowPresetMenu(false);
  };

  // Get emoji for token
  const getTokenEmoji = (name) => {
    const key = name.toUpperCase().replace(/[^A-Z0-9]/g, '');
    return TOKEN_EMOJIS[key] || 'ü™ô';
  };

  // Get display token name
  const displayToken = tokenName || 'SOLUSDT';
  const emoji = getTokenEmoji(displayToken);

  return (
    <div className="min-h-screen bg-[#0f0f0f] px-6 py-10 text-white">
      {/* Crypto Ticker */}
      <div className="w-full max-w-7xl mx-auto mb-8">
        <CryptoTicker />
      </div>

      {/* Main Content - Three Column Layout */}
      <div className="grid grid-cols-12 gap-6 h-[calc(100vh-9rem)]">
        {/* Left Column - Live Trades Feed */}
        <div className="col-span-3">
          <div className="bg-gradient-to-br from-zinc-800/80 via-zinc-900/80 to-black/80 rounded-2xl p-5 h-full border border-blue-500/20 shadow-2xl backdrop-blur-xl relative overflow-hidden">
            <div className="absolute -inset-0.5 pointer-events-none rounded-2xl border-2 border-blue-500/10 blur-xl" />
            {/* Tab Switcher */}
            <div className="mb-3">
              <StrategyTabSwitcher
                tabs={strategyTabs}
                active={activeStrategyTab}
                onSelect={setActiveStrategyTab}
                onAdd={handleAddTab}
                onRemove={handleRemoveTab}
                discoverTab={discoverTab}
              />
              {showPresetMenu && (
                <div className="absolute z-20 mt-2 left-0 bg-zinc-900/95 border border-blue-500/20 rounded-xl shadow-xl p-2 w-56">
                  <div className="text-xs text-zinc-400 px-2 pb-2">Add a preset strategy:</div>
                  {PRESET_STRATEGIES.filter(p => !strategyTabs.includes(p)).map(preset => (
                    <button
                      key={preset}
                      className="block w-full text-left px-4 py-2 rounded-lg hover:bg-blue-500/10 text-white font-semibold mb-1"
                      onClick={() => handleSelectPreset(preset)}
                    >
                      {preset}
                    </button>
                  ))}
                  <button
                    className="block w-full text-left px-4 py-2 rounded-lg text-zinc-400 hover:bg-zinc-800 mt-1"
                    onClick={() => setShowPresetMenu(false)}
                  >
                    Cancel
                  </button>
                </div>
              )}
            </div>
            {discoverTab && activeStrategyTab ? (
              <div className="overflow-y-auto h-[calc(100%-2.5rem)] custom-scrollbar">
                <table className="w-full text-sm rounded-xl overflow-hidden">
                  <thead>
                    <tr className="text-blue-300/80 border-b border-blue-500/10 bg-gradient-to-r from-blue-900/20 to-transparent">
                      <th className="text-left font-bold py-2 px-2 tracking-wide">Time</th>
                      <th className="text-left font-bold py-2 px-2 tracking-wide">Token</th>
                      <th className="text-right font-bold py-2 px-2 tracking-wide">MC</th>
                    </tr>
                  </thead>
                  <tbody>
                    {feed.map((trade, idx) => (
                      <tr
                        key={idx}
                        className="group hover:bg-blue-500/10 cursor-pointer transition-all duration-200 rounded-xl shadow-sm hover:scale-[1.01] border-l-4 border-transparent hover:border-blue-400/60"
                        onClick={() => navigate(`/trade/${trade.token.name}`)}
                      >
                        <td className="py-2 px-2 text-blue-200/80 font-mono">{trade.time}</td>
                        <td className="flex items-center gap-2 py-2 px-2">
                          <span className="text-2xl drop-shadow-[0_2px_8px_rgba(59,130,246,0.15)]">{trade.token.icon}</span>
                          <span className="font-bold text-white truncate max-w-[100px] group-hover:text-blue-400 transition-colors duration-200">{trade.token.name}</span>
                        </td>
                        <td className="text-right text-blue-100 font-mono px-2">{trade.mc}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="text-center text-zinc-400 mt-8">No tokens to show. Add a strategy to get started.</div>
            )}
          </div>
        </div>

        {/* Center Column - TradingView Chart + Trades Table */}
        <div className="col-span-6 flex flex-col gap-6">
          <div className="bg-gradient-to-br from-blue-900/30 via-zinc-900/80 to-black/80 rounded-2xl p-6 border border-purple-500/20 shadow-2xl backdrop-blur-xl relative overflow-hidden flex-1 flex flex-col">
            <div className="absolute -inset-0.5 pointer-events-none rounded-2xl border-2 border-purple-500/10 blur-xl" />
            {/* Token Name and Emoji Header */}
            <div className="flex-1 mb-6">
              <ChartArea tokenName={displayToken} emoji={emoji} fullName={tokenName || displayToken} />
            </div>
          </div>
          {/* Trades Table */}
          <div className="bg-gradient-to-br from-zinc-800/80 via-zinc-900/80 to-black/80 rounded-2xl p-6 border border-blue-500/20 shadow-xl backdrop-blur-xl relative overflow-hidden">
            <div className="absolute -inset-0.5 pointer-events-none rounded-2xl border-2 border-blue-500/10 blur-xl" />
            <table className="w-full text-sm rounded-xl overflow-hidden">
              <thead>
                <tr className="text-purple-300/80 border-b border-purple-500/10 bg-gradient-to-r from-purple-900/20 to-transparent">
                  <th className="text-left font-bold py-2 px-2 tracking-wide">Age</th>
                  <th className="text-left font-bold py-2 px-2 tracking-wide">Type</th>
                  <th className="text-left font-bold py-2 px-2 tracking-wide">MC</th>
                  <th className="text-left font-bold py-2 px-2 tracking-wide">Amount</th>
                  <th className="text-left font-bold py-2 px-2 tracking-wide">Total USD</th>
                  <th className="text-left font-bold py-2 px-2 tracking-wide">Trader</th>
                </tr>
              </thead>
              <tbody>
                {tradesTable.map((trade, idx) => (
                  <tr key={idx} className={`border-b border-zinc-800 last:border-0 hover:bg-purple-500/10 transition-all duration-200 rounded-xl shadow-sm hover:scale-[1.01] ${trade.type === 'Buy' ? 'border-l-4 border-green-400/60' : 'border-l-4 border-red-400/60'}`}>
                    <td className="py-2 px-2 text-purple-200/80 font-mono">{trade.age}</td>
                    <td className={`py-2 px-2 font-bold ${trade.type === 'Buy' ? 'text-green-400' : 'text-red-400'}`}>{trade.type}</td>
                    <td className="py-2 px-2 text-white">{trade.mc}</td>
                    <td className="py-2 px-2 text-white">{trade.amount}</td>
                    <td className={`py-2 px-2 font-mono ${trade.type === 'Buy' ? 'text-green-300' : 'text-red-300'}`}>{trade.usd}</td>
                    <td className="py-2 px-2 text-blue-300 font-mono">{trade.trader}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Right Column - Trading Terminal */}
        <div className="col-span-3">
          <div className="bg-gradient-to-br from-purple-900/30 via-zinc-900/80 to-black/80 rounded-2xl p-6 h-full border border-purple-500/20 shadow-2xl backdrop-blur-xl relative overflow-hidden flex flex-col">
            <div className="absolute -inset-0.5 pointer-events-none rounded-2xl border-2 border-purple-500/10 blur-xl" />
            <h3 className="text-xl font-extrabold mb-4 bg-gradient-to-r from-white via-purple-200 to-white bg-clip-text text-transparent drop-shadow-[0_2px_8px_rgba(168,85,247,0.15)]">Trading Terminal</h3>
            <div className="space-y-4 flex-1">
              {/* Buy/Sell Buttons */}
              <div className="flex gap-2">
                <button
                  className={`flex-1 py-3 rounded-lg font-bold text-lg shadow-lg transition-all duration-200 ${
                    side === 'buy' ? 'bg-gradient-to-r from-green-400 to-blue-400 text-black scale-105 ring-2 ring-green-300/40' : 'bg-zinc-800/80 text-white hover:bg-green-400/10'
                  }`}
                  onClick={() => setSide('buy')}
                >
                  Buy
                </button>
                <button
                  className={`flex-1 py-3 rounded-lg font-bold text-lg shadow-lg transition-all duration-200 ${
                    side === 'sell' ? 'bg-gradient-to-r from-red-400 to-purple-400 text-white scale-105 ring-2 ring-red-300/40' : 'bg-zinc-800/80 text-white hover:bg-red-400/10'
                  }`}
                  onClick={() => setSide('sell')}
                >
                  Sell
                </button>
              </div>
              {/* Order Type */}
              <div className="flex gap-2">
                <button
                  className={`flex-1 py-2 rounded-lg font-semibold shadow transition-all duration-200 ${
                    orderType === 'limit' ? 'bg-gradient-to-r from-blue-500 to-purple-500 text-white scale-105 ring-2 ring-blue-300/40' : 'bg-zinc-800/80 text-white hover:bg-blue-400/10'
                  }`}
                  onClick={() => setOrderType('limit')}
                >
                  Limit
                </button>
                <button
                  className={`flex-1 py-2 rounded-lg font-semibold shadow transition-all duration-200 ${
                    orderType === 'market' ? 'bg-gradient-to-r from-green-500 to-blue-500 text-white scale-105 ring-2 ring-green-300/40' : 'bg-zinc-800/80 text-white hover:bg-green-400/10'
                  }`}
                  onClick={() => setOrderType('market')}
                >
                  Market
                </button>
                <button
                  className={`flex-1 py-2 rounded-lg font-semibold shadow transition-all duration-200 ${
                    orderType === 'stop' ? 'bg-gradient-to-r from-red-500 to-purple-500 text-white scale-105 ring-2 ring-red-300/40' : 'bg-zinc-800/80 text-white hover:bg-red-400/10'
                  }`}
                  onClick={() => setOrderType('stop')}
                >
                  Stop
                </button>
              </div>
              {/* Order Form */}
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-zinc-400 mb-1">Amount</label>
                  <input
                    type="text"
                    value={buyAmount}
                    onChange={(e) => setBuyAmount(e.target.value)}
                    className="w-full px-4 py-2 rounded-lg bg-zinc-800/80 border border-zinc-700 text-white focus:ring-2 focus:ring-blue-500/40 focus:border-blue-500/40 transition-all"
                    placeholder="0.00"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-zinc-400 mb-1">Price</label>
                  <input
                    type="text"
                    className="w-full px-4 py-2 rounded-lg bg-zinc-800/80 border border-zinc-700 text-white focus:ring-2 focus:ring-blue-500/40 focus:border-blue-500/40 transition-all"
                    placeholder="0.00"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-zinc-400 mb-1">Total</label>
                  <input
                    type="text"
                    className="w-full px-4 py-2 rounded-lg bg-zinc-800/80 border border-zinc-700 text-white focus:ring-2 focus:ring-blue-500/40 focus:border-blue-500/40 transition-all"
                    placeholder="0.00"
                  />
                </div>
              </div>
              {/* Place Order Button */}
              <button
                className={`w-full py-3 rounded-lg font-bold text-lg shadow-lg transition-all duration-200 ${
                  side === 'buy'
                    ? 'bg-gradient-to-r from-green-400 to-blue-400 text-black hover:scale-105'
                    : 'bg-gradient-to-r from-red-400 to-purple-400 text-white hover:scale-105'
                }`}
              >
                Place {side === 'buy' ? 'Buy' : 'Sell'} Order
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
