import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const EXAMPLE_PROMPTS = [
  "Alert me when a token is mentioned in 3+ alpha groups within 30 minutes",
  "Show tokens with 5+ smart money buys in the last hour",
  "Find tokens with 300%+ volume spike and no exit liquidity",
  "Monitor GambleFi tokens with rising social sentiment"
];

const previewTokens = [
  { name: 'PAWPAW', risk: 'Low', meta: 'GambleFi', roi: '+24%', volume: '$123K', holders: '1.2K' },
  { name: 'BABY BOB', risk: 'Low', meta: 'GambleFi', roi: '+34%', volume: '$456K', holders: '2.4K' },
];

export default function CreateTab({ goBack }) {
  const [prompt, setPrompt] = useState('');
  const [strategy, setStrategy] = useState(null);
  const [isTyping, setIsTyping] = useState(false);

  const handlePromptSubmit = async () => {
    setIsTyping(true);
    await new Promise(resolve => setTimeout(resolve, 1000));
    const newStrategy = {
      name: "Custom Strategy",
      description: prompt,
      rules: parsePromptToRules(prompt),
      status: 'active'
    };
    setStrategy(newStrategy);
    setIsTyping(false);
  };

  const parsePromptToRules = (text) => {
    const rules = [];
    if (text.toLowerCase().includes('telegram') || text.toLowerCase().includes('group')) {
      rules.push({
        type: 'telegram',
        description: 'Monitor Telegram groups for token mentions',
        icon: '📱'
      });
    }
    if (text.toLowerCase().includes('volume')) {
      rules.push({
        type: 'volume',
        description: 'Track volume spikes',
        icon: '📈'
      });
    }
    if (text.toLowerCase().includes('smart') || text.toLowerCase().includes('buy')) {
      rules.push({
        type: 'smart-money',
        description: 'Monitor smart money wallets',
        icon: '🧠'
      });
    }
    if (text.toLowerCase().includes('gamblefi')) {
      rules.push({
        type: 'narrative',
        description: 'Track GambleFi narrative',
        icon: '🎲'
      });
    }
    return rules;
  };

  return (
    <div className="relative min-h-screen w-full bg-gradient-to-b from-black via-purple-950/5 to-black">
      <DndProvider backend={HTML5Backend}>
        <div className="relative z-10 p-6">
          {/* Background Gradient Effects */}
          <div className="absolute inset-0 bg-[radial-gradient(circle_at_top_right,_rgba(168,85,247,0.15),_transparent_50%)]" />
          <div className="absolute inset-0 bg-[radial-gradient(circle_at_bottom_left,_rgba(168,85,247,0.1),_transparent_50%)]" />

          {/* Back Button */}
          {goBack && (
            <div className="pt-6 pl-6 relative z-10">
              <button
                onClick={goBack}
                className="group flex items-center text-sm text-purple-400 bg-purple-500/5 px-4 py-2 rounded-lg backdrop-blur-sm transition-all hover:bg-purple-500/10"
              >
                <span className="mr-2 transition-transform group-hover:-translate-x-1">←</span>
                Back to Home
              </button>
            </div>
          )}

          <div className="max-w-5xl mx-auto px-6 py-8 relative z-10">
            {/* Header */}
            <div className="text-center mb-12">
              <h2 className="text-4xl font-bold mb-3 bg-gradient-to-r from-purple-400 via-purple-300 to-purple-400 text-transparent bg-clip-text">
                Create Your Strategy
              </h2>
              <p className="text-purple-300/60">
                Describe your trading strategy in natural language
              </p>
            </div>

            {/* Natural Language Input */}
            <div className="mb-12">
              <div className="relative group">
                <div className="absolute -inset-1 bg-gradient-to-r from-purple-500/50 via-purple-500/25 to-purple-500/50 rounded-xl blur-lg group-hover:blur-xl transition-all duration-500 group-hover:opacity-75 opacity-50" />
                <div className="relative">
                  <textarea
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    placeholder="Try: Alert me when a token is mentioned in 3+ alpha groups within 30 minutes..."
                    className="w-full h-40 bg-black/40 backdrop-blur-xl rounded-xl p-6 text-lg text-white placeholder:text-zinc-600 focus:outline-none focus:ring-2 focus:ring-purple-500/50 resize-none border border-purple-500/20"
                  />
                  <button
                    onClick={handlePromptSubmit}
                    disabled={!prompt || isTyping}
                    className={`absolute bottom-4 right-4 px-6 py-2.5 rounded-lg font-medium transition-all duration-300 ${
                      !prompt || isTyping
                        ? 'bg-zinc-800/50 text-zinc-600 cursor-not-allowed'
                        : 'bg-purple-500 text-white hover:bg-purple-600 hover:shadow-[0_0_20px_rgba(168,85,247,0.3)]'
                    }`}
                  >
                    {isTyping ? (
                      <span className="flex items-center">
                        <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" fill="none" viewBox="0 0 24 24">
                          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                        </svg>
                        Processing
                      </span>
                    ) : 'Create Strategy'}
                  </button>
                </div>
              </div>

              {/* Example Prompts */}
              <div className="mt-6">
                <p className="text-sm text-purple-300/60 mb-3">Or try one of these examples:</p>
                <div className="flex flex-wrap gap-2">
                  {EXAMPLE_PROMPTS.map((example, i) => (
                    <button
                      key={i}
                      onClick={() => setPrompt(example)}
                      className="text-sm bg-purple-500/10 text-purple-300 px-4 py-2 rounded-full hover:bg-purple-500/20 transition-all duration-300 border border-purple-500/20 hover:border-purple-500/40"
                    >
                      {example}
                    </button>
                  ))}
                </div>
              </div>
            </div>

            {strategy && (
              <div className="space-y-8 animate-fadeIn">
                {/* Strategy Preview */}
                <div className="relative group">
                  <div className="absolute -inset-1 bg-gradient-to-r from-purple-500/30 via-purple-500/20 to-purple-500/30 rounded-xl blur-lg opacity-50" />
                  <div className="relative bg-black/40 backdrop-blur-xl rounded-xl p-8 border border-purple-500/20">
                    <h3 className="text-2xl font-bold mb-4 bg-gradient-to-r from-purple-400 to-purple-300 text-transparent bg-clip-text">
                      Strategy Preview
                    </h3>
                    <p className="text-purple-300/80 mb-6">{strategy.description}</p>

                    <div className="space-y-3 mb-8">
                      {strategy.rules.map((rule, i) => (
                        <div key={i} className="flex items-center gap-4 bg-purple-500/5 p-4 rounded-lg border border-purple-500/20">
                          <span className="text-2xl">{rule.icon}</span>
                          <span className="text-purple-200">{rule.description}</span>
                        </div>
                      ))}
                    </div>

                    <div className="flex gap-4">
                      <button className="flex-1 bg-purple-500 text-white py-3 rounded-lg hover:bg-purple-600 transition-all duration-300 hover:shadow-[0_0_20px_rgba(168,85,247,0.3)]">
                        Deploy Strategy
                      </button>
                      <button className="flex-1 bg-purple-500/10 text-purple-300 py-3 rounded-lg hover:bg-purple-500/20 transition-all duration-300 border border-purple-500/20">
                        Edit Strategy
                      </button>
                    </div>
                  </div>
                </div>

                {/* Live Preview */}
                <div className="relative group">
                  <div className="absolute -inset-1 bg-gradient-to-r from-purple-500/30 via-purple-500/20 to-purple-500/30 rounded-xl blur-lg opacity-50" />
                  <div className="relative bg-black/40 backdrop-blur-xl rounded-xl p-8 border border-purple-500/20">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-2xl font-bold bg-gradient-to-r from-purple-400 to-purple-300 text-transparent bg-clip-text">
                        Live Preview
                      </h3>
                      <div className="flex items-center gap-2">
                        <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
                        <span className="text-green-400 text-sm">LIVE</span>
                      </div>
                    </div>

                    <div className="space-y-4">
                      {previewTokens.map((t) => (
                        <div 
                          key={t.name}
                          className="group/token bg-purple-500/5 rounded-xl p-6 hover:bg-purple-500/10 transition-all duration-300 cursor-pointer border border-purple-500/20 hover:border-purple-500/40"
                        >
                          <div className="flex justify-between items-start mb-3">
                            <div>
                              <h4 className="text-xl font-bold text-white group-hover/token:text-purple-300 transition-colors">
                                {t.name}
                              </h4>
                              <p className="text-sm text-purple-300/60">
                                Risk: {t.risk} • Meta: {t.meta}
                              </p>
                            </div>
                            <div className="text-right">
                              <div className="text-green-400 font-bold text-xl">{t.roi}</div>
                              <div className="text-sm text-purple-300/60">24h Change</div>
                            </div>
                          </div>
                          <div className="flex gap-4 text-sm text-purple-300/60">
                            <div>Vol: {t.volume}</div>
                            <div>Holders: {t.holders}</div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </DndProvider>
    </div>
  );
}
