import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

// Mock data for data sources
const DATA_SOURCES = [
  { name: 'Telegram', connected: false, icon: '📱' },
  { name: 'Smart Wallets', connected: false, icon: '💼' },
  { name: 'Token Market Data', connected: true, icon: '📊' }
];

// Available filters
const AVAILABLE_FILTERS = [
  { label: 'Token mentioned in Telegram', tooltip: 'Triggered when a token appears in your connected chats' },
  { label: 'Smart Wallet Buys', tooltip: 'Uses wallets you\'ve imported to detect buys' },
  { label: 'Price Retracement', tooltip: 'Triggers when a token drops from its ATH or a recent peak' },
  { label: 'Volume Spike', tooltip: 'Detects tokens with sudden volume increases' }
];

export default function CreateTab({ goBack }) {
  const [prompt, setPrompt] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [strategy, setStrategy] = useState(null);
  const [previewTokens, setPreviewTokens] = useState([]);

  const handlePromptSubmit = async () => {
    setIsTyping(true);
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1500));
    setStrategy({
      description: prompt,
      rules: [
        { icon: '🔍', description: 'Monitor Telegram groups' },
        { icon: '💼', description: 'Track smart wallet activity' },
        { icon: '📊', description: 'Analyze market data' }
      ]
    });
    setPreviewTokens([
      { name: 'TOKEN1', risk: 'Medium', meta: 'Trending', roi: '+15%', volume: '$1.2M', holders: '1.5K' },
      { name: 'TOKEN2', risk: 'Low', meta: 'Established', roi: '+8%', volume: '$800K', holders: '2.1K' }
    ]);
    setIsTyping(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-zinc-900 via-black to-zinc-900 p-6">
      <DndProvider backend={HTML5Backend}>
        {/* Data Sources Banner */}
        <section className="mb-8 max-w-6xl mx-auto">
          <div className="flex flex-wrap gap-4 items-center justify-center">
            {DATA_SOURCES.map((source) => (
              <div
                key={source.name}
                className={`flex items-center gap-2 px-4 py-2 rounded-lg backdrop-blur-sm transition-all border text-base font-medium shadow-sm ${
                  source.connected
                    ? 'bg-emerald-500/10 text-emerald-400 border-emerald-500/20'
                    : 'bg-zinc-900/50 text-zinc-500 border-zinc-800 hover:border-blue-500/20 cursor-pointer'
                }`}
              >
                <span className="text-lg">{source.icon}</span>
                <span>{source.name}</span>
                {!source.connected && (
                  <button className="ml-2 text-sm text-blue-400 hover:text-blue-300">
                    Connect
                  </button>
                )}
              </div>
            ))}
          </div>
        </section>

        <div className="flex flex-col lg:flex-row gap-8 max-w-6xl mx-auto">
          {/* Main Column */}
          <div className="flex-1 flex flex-col gap-8">
            {/* Strategy Input Card */}
            <section className="bg-black/40 rounded-xl p-8 border border-zinc-800 shadow-lg">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-2xl font-bold">Describe Your Strategy</h2>
                {goBack && (
                  <button
                    onClick={goBack}
                    className="group flex items-center text-sm text-blue-400 bg-blue-500/5 px-4 py-2 rounded-lg backdrop-blur-sm transition-all hover:bg-blue-500/10"
                  >
                    <span className="mr-2 transition-transform group-hover:-translate-x-1">←</span>
                    Back
                  </button>
                )}
              </div>
              <textarea
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                placeholder="Example: Alert me when a token is mentioned in 3 Telegram groups and bought by 2 smart wallets"
                className="w-full h-32 bg-zinc-900/60 backdrop-blur-xl rounded-xl p-4 text-lg text-white placeholder:text-zinc-500 focus:outline-none focus:ring-2 focus:ring-blue-500/50 resize-none border border-zinc-800 mb-4"
              />
              <button
                onClick={handlePromptSubmit}
                disabled={!prompt || isTyping}
                className={`mt-2 px-6 py-2.5 rounded-lg font-medium transition-all duration-300 ${
                  !prompt || isTyping
                    ? 'bg-zinc-800/50 text-zinc-600 cursor-not-allowed'
                    : 'bg-gradient-to-r from-blue-500 to-purple-500 text-white hover:shadow-[0_0_20px_rgba(59,130,246,0.3)]'
                }`}
              >
                {isTyping ? (
                  <span className="flex items-center">
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                    </svg>
                    Processing
                  </span>
                ) : 'Create Strategy'}
              </button>
              {/* Inline Warnings */}
              {prompt.includes('telegram') && !DATA_SOURCES[0].connected && (
                <div className="mt-4 p-4 bg-amber-500/10 border border-amber-500/20 rounded-lg text-amber-400">
                  <p>To use Telegram group logic, please connect your groups first.</p>
                  <button className="mt-2 text-sm text-amber-300 hover:text-amber-200">
                    Connect Groups
                  </button>
                </div>
              )}
              {prompt.includes('wallet') && !DATA_SOURCES[1].connected && (
                <div className="mt-4 p-4 bg-amber-500/10 border border-amber-500/20 rounded-lg text-amber-400">
                  <p>You need to import smart wallets to use wallet-based logic.</p>
                  <button className="mt-2 text-sm text-amber-300 hover:text-amber-200">
                    Import Wallets
                  </button>
                </div>
              )}
            </section>

            {/* Strategy Preview & Live Preview */}
            <section className="bg-black/40 rounded-xl p-8 border border-zinc-800 shadow-lg">
              <h3 className="text-xl font-bold mb-4">Preview</h3>
              {strategy ? (
                <>
                  {/* Strategy Preview */}
                  <div className="mb-8">
                    <h4 className="text-lg font-semibold mb-2">Strategy</h4>
                    <p className="text-zinc-400 mb-4">{strategy.description}</p>
                    <div className="space-y-2">
                      {strategy.rules.map((rule, i) => (
                        <div key={i} className="flex items-center gap-3 bg-zinc-900/50 p-3 rounded-lg border border-zinc-800">
                          <span className="text-2xl">{rule.icon}</span>
                          <span className="text-zinc-300">{rule.description}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                  {/* Live Preview */}
                  <div>
                    <h4 className="text-lg font-semibold mb-2">Live Preview</h4>
                    <div className="flex items-center gap-2 mb-4">
                      <div className="w-2 h-2 bg-emerald-500 rounded-full animate-pulse" />
                      <span className="text-emerald-400 text-sm">LIVE</span>
                    </div>
                    <div className="space-y-3">
                      {previewTokens.map((t) => (
                        <div 
                          key={t.name}
                          className="group/token bg-zinc-900/50 rounded-xl p-4 hover:bg-zinc-800/50 transition-all duration-300 cursor-pointer border border-zinc-800 hover:border-blue-500/20"
                        >
                          <div className="flex justify-between items-start mb-2">
                            <div>
                              <h5 className="text-lg font-bold text-white group-hover/token:text-blue-300 transition-colors">
                                {t.name}
                              </h5>
                              <p className="text-xs text-zinc-500">
                                Risk: {t.risk} • Meta: {t.meta}
                              </p>
                            </div>
                            <div className="text-right">
                              <div className="text-emerald-400 font-bold text-lg">{t.roi}</div>
                              <div className="text-xs text-zinc-500">24h Change</div>
                            </div>
                          </div>
                          <div className="flex gap-4 text-xs text-zinc-500">
                            <div>Vol: {t.volume}</div>
                            <div>Holders: {t.holders}</div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                  {/* Action Buttons */}
                  <div className="flex flex-col sm:flex-row gap-4 mt-8">
                    <button
                      className="flex-1 bg-gradient-to-r from-blue-500 to-purple-500 text-white py-3 rounded-lg font-semibold shadow hover:shadow-lg transition-all duration-300"
                      onClick={() => alert('Strategy published!')}
                    >
                      Publish Strategy
                    </button>
                    <button
                      className="flex-1 bg-zinc-900 text-zinc-400 py-3 rounded-lg font-semibold border border-zinc-800 hover:bg-zinc-800 transition-all duration-300"
                      onClick={() => alert('Strategy added to your feed!')}
                    >
                      Add to My Feed
                    </button>
                  </div>
                </>
              ) : (
                <p className="text-zinc-500">Enter a strategy above to see a preview.</p>
              )}
            </section>
          </div>

          {/* Filters Panel */}
          <aside className="w-full lg:w-80 flex-shrink-0">
            <section className="bg-black/40 rounded-xl p-6 border border-zinc-800 shadow-lg sticky top-8">
              <h3 className="text-lg font-bold mb-4">Available Filters</h3>
              <div className="space-y-3">
                {AVAILABLE_FILTERS.map((filter, i) => (
                  <div
                    key={i}
                    className="group/filter p-3 rounded-lg bg-zinc-900/50 border border-zinc-800 hover:border-blue-500/20 transition-all duration-300"
                  >
                    <div className="flex items-center gap-2">
                      <span className="text-blue-400">•</span>
                      <span className="text-zinc-300 group-hover/filter:text-blue-300 transition-colors">
                        {filter.label}
                      </span>
                    </div>
                    <p className="mt-1 text-xs text-zinc-500">
                      {filter.tooltip}
                    </p>
                  </div>
                ))}
              </div>
            </section>
          </aside>
        </div>
      </DndProvider>
    </div>
  );
}
