import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import WelcomeScreen from './components/WelcomeScreen';
import TemplatesTab from './components/TemplatesTab';
import CreateTab from './components/CreateTab';
import DiscoverTab from './components/DiscoverTab';
import DashboardTab from './components/DashboardTab';
import BuilderTab from './components/BuilderTab';
import FeedPage from './pages/FeedPage';

const NAV = [
  { name: 'Feed', onClick: () => {} },
  { name: 'FAQ', onClick: () => {} },
  { name: 'Import', onClick: () => {} },
];

const TABS = [
  { name: 'Welcome', component: <WelcomeScreen /> },
  { name: 'Templates', component: <TemplatesTab /> },
  { name: 'Create', component: <CreateTab /> },
  { name: 'Discover', component: <DiscoverTab /> },
  { name: 'Dashboard', component: <DashboardTab /> },
  { name: 'Feed', component: <FeedPage /> },
];

export default function App() {
  const [activeTab, setActiveTab] = useState(0);
  const [showBuilder, setShowBuilder] = useState(false);
  const [selectedTemplate, setSelectedTemplate] = useState(null);

  // Handler to set tab by name
  const handleCardClick = (tabName) => {
    const idx = TABS.findIndex(tab => tab.name === tabName);
    if (idx !== -1) setActiveTab(idx);
  };

  const goBack = () => {
    setShowBuilder(false);
    setActiveTab(0);
  };

  const handleTryFlow = (template) => {
    setSelectedTemplate(template);
    setShowBuilder(true);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen bg-transparent">
        {/* Top Navigation Bar */}
        <div className="flex justify-between items-center px-6 py-4 bg-black/80 backdrop-blur-md shadow-sm border-b border-neutral-800 sticky top-0 z-10">
          <div className="flex items-center space-x-4">
            <h1 className="text-xl font-bold text-white">
              SCOPE<span className="text-purple-500">Â²</span>
            </h1>
            <div className="flex space-x-4 text-sm text-white/80">
              {NAV.map((item, idx) => (
                <button
                  key={item.name}
                  className={`font-semibold px-4 py-2 rounded-full transition-all duration-200 shadow-inner hover:text-white/90`}
                  onClick={item.onClick}
                >
                  {item.name}
                </button>
              ))}
            </div>
          </div>
          <div className="text-sm text-right text-purple-400 italic drop-shadow-[0_0_6px_rgba(168,85,247,0.7)]">
            <span className="font-semibold text-purple-500">news2pump</span> powered
          </div>
        </div>
        <main className="flex flex-col items-center justify-center flex-1 w-full">
          {showBuilder ? (
            <BuilderTab goBack={goBack} template={selectedTemplate} />
          ) : activeTab === 0 ? <WelcomeScreen onCardClick={handleCardClick} /> :
            activeTab === 1 ? <TemplatesTab goBack={goBack} onTryFlow={handleTryFlow} /> :
            activeTab === 2 ? <CreateTab goBack={goBack} /> :
            activeTab === 3 ? <DiscoverTab goBack={goBack} /> :
            activeTab === 4 ? <DashboardTab goBack={goBack} /> :
            TABS[activeTab].component}
        </main>
      </div>
    </DndProvider>
  );
}
