import React, { useState } from 'react';
import WelcomeScreen from './components/WelcomeScreen';
import TemplatesTab from './components/TemplatesTab';
import CreateTab from './components/CreateTab';
import DiscoverTab from './components/DiscoverTab';
import DashboardTab from './components/DashboardTab';

const TABS = [
  { name: 'Welcome', component: <WelcomeScreen /> },
  { name: 'Templates', component: <TemplatesTab /> },
  { name: 'Create', component: <CreateTab /> },
  { name: 'Discover', component: <DiscoverTab /> },
  { name: 'Dashboard', component: <DashboardTab /> },
];

export default function App() {
  const [activeTab, setActiveTab] = useState(0);

  return (
    <div className="min-h-screen bg-transparent">
      {/* Top Navigation Bar */}
      <div className="flex justify-between items-center px-6 py-4 bg-black/80 backdrop-blur-md shadow-sm border-b border-neutral-800 sticky top-0 z-10">
        <div className="flex space-x-4 text-sm text-white/80">
          {TABS.map((tab, idx) => (
            <button
              key={tab.name}
              className={`font-semibold px-4 py-2 rounded-full transition-all duration-200 shadow-inner
                ${activeTab === idx ? 'text-white bg-neutral-900' : 'hover:text-white/90'}`}
              onClick={() => setActiveTab(idx)}
            >
              {tab.name}
            </button>
          ))}
        </div>
        <div className="text-xs text-right text-purple-400 italic mr-4 mt-2 drop-shadow-[0_0_6px_rgba(168,85,247,0.7)]">
          powered by <span className="font-semibold text-purple-500">News2Pump</span>
        </div>
      </div>
      <main className="flex flex-col items-center justify-center flex-1 w-full">
        {TABS[activeTab].component}
      </main>
    </div>
  );
}
