import React from 'react';
import { Sliders, EyeOff, Columns, Info } from 'lucide-react';

const SOL_LOGO = (
  <svg width="28" height="28" viewBox="0 0 32 32" fill="none">
    <defs>
      <linearGradient id="sol1" x1="0" y1="0" x2="32" y2="32" gradientUnits="userSpaceOnUse">
        <stop stopColor="#9945FF" />
        <stop offset="0.5" stopColor="#14F195" />
        <stop offset="1" stopColor="#00FFA3" />
      </linearGradient>
    </defs>
    <rect width="32" height="32" rx="16" fill="#181A20" />
    <g filter="url(#glow)">
      <rect x="7" y="9" width="18" height="3" rx="1.5" fill="url(#sol1)" />
      <rect x="7" y="15" width="18" height="3" rx="1.5" fill="url(#sol1)" opacity="0.7" />
      <rect x="7" y="21" width="18" height="3" rx="1.5" fill="url(#sol1)" opacity="0.5" />
    </g>
    <filter id="glow" x="-20%" y="-20%" width="140%" height="140%">
      <feGaussianBlur stdDeviation="2" result="coloredBlur" />
      <feMerge>
        <feMergeNode in="coloredBlur" />
        <feMergeNode in="SourceGraphic" />
      </feMerge>
    </filter>
  </svg>
);

export default function QuickBuyBar({ amount, onAmountChange }) {
  return (
    <div className="relative flex items-center gap-8 w-full max-w-3xl mx-auto group transition-all duration-300 hover:scale-[1.015]" style={{ zIndex: 20 }}>
      {/* Animated Gradient Glow */}
      <div className="absolute -inset-2 rounded-2xl pointer-events-none blur-[10px] opacity-70 animate-pulse-glow" style={{ background: 'linear-gradient(90deg, #38bdf8 0%, #6366f1 60%, #f43f5e 100%)', zIndex: 1 }} />
      {/* Glassy Bar */}
      <div className="flex items-center gap-4 bg-zinc-900/70 backdrop-blur-2xl rounded-2xl border border-blue-400/30 shadow-2xl px-5 py-2 relative z-10 min-w-[200px] min-h-[48px] ring-1 ring-blue-400/10 group-hover:ring-2 group-hover:ring-blue-400/30 transition-all duration-300">
        <span className="font-bold text-sm bg-gradient-to-r from-blue-400 via-green-400 to-blue-400 bg-clip-text text-transparent tracking-wide select-none drop-shadow">quick buy</span>
        <div className="flex items-center gap-2 bg-zinc-900/80 rounded-full px-3 py-1 border border-blue-400/20 shadow-inner">
          <span className="w-7 h-7 flex items-center justify-center">{SOL_LOGO}</span>
          <input
            type="text"
            inputMode="decimal"
            pattern="[0-9.]*"
            value={amount}
            onChange={e => onAmountChange(e.target.value)}
            className="w-16 bg-transparent text-white text-lg font-bold outline-none border-none text-right px-2 rounded-full focus:ring-2 focus:ring-blue-400/70 focus:ring-offset-2 focus:ring-offset-zinc-900 transition-all duration-200 appearance-none shadow focus:shadow-blue-400/20"
            style={{ MozAppearance: 'textfield', boxShadow: '0 0 0 2px #6366f122' }}
            autoComplete="off"
            placeholder="0.00"
          />
        </div>
      </div>
      {/* Controls Row */}
      <div className="flex items-center gap-8 ml-6 z-10">
        <button className="flex flex-col items-center text-zinc-400 hover:text-blue-400 transition group" title="Settings">
          <Sliders size={26} className="drop-shadow group-hover:scale-110 transition-transform" />
          <span className="text-xs mt-1 group-hover:text-blue-400 font-semibold tracking-wide">settings</span>
        </button>
        <button className="flex flex-col items-center text-zinc-400 hover:text-blue-400 transition group" title="Hidden">
          <EyeOff size={26} className="drop-shadow group-hover:scale-110 transition-transform" />
          <span className="text-xs mt-1 group-hover:text-blue-400 font-semibold tracking-wide">hidden</span>
        </button>
        <button className="flex flex-col items-center text-zinc-400 hover:text-blue-400 transition group" title="Edit Columns">
          <Columns size={26} className="drop-shadow group-hover:scale-110 transition-transform" />
          <span className="text-xs mt-1 group-hover:text-blue-400 font-semibold tracking-wide">edit columns</span>
        </button>
        <button className="flex flex-col items-center text-zinc-400 hover:text-blue-400 transition group" title="Legend">
          <Info size={26} className="drop-shadow group-hover:scale-110 transition-transform" />
          <span className="text-xs mt-1 group-hover:text-blue-400 font-semibold tracking-wide">legend</span>
        </button>
      </div>
    </div>
  );
}

/*
Add this to your global CSS (e.g., index.css or tailwind config):
@keyframes pulse-glow {
  0%, 100% { opacity: 0.7; filter: blur(10px); }
  50% { opacity: 1; filter: blur(16px); }
}
.animate-pulse-glow {
  animation: pulse-glow 2.5s infinite;
}
*/
