import React, { useState } from 'react';
import { User, Shield, Star, Zap, DollarSign, TrendingUp, Info, Plus } from 'lucide-react';

const PRESETS = ['Preset 1', 'Preset 2', 'Preset 3'];

export default function TerminalOrderPanel({ tokenName = 'TOKEN' }) {
  const [side, setSide] = useState('buy');
  const [orderType, setOrderType] = useState('market');
  const [amount, setAmount] = useState('0.0');
  const [preset, setPreset] = useState(0);
  const quickAmounts = ['0.01', '0.1', '1', '10'];

  return (
    <div className="bg-gradient-to-br from-[#23262F]/80 via-[#18181c]/90 to-[#23262F]/80 rounded-2xl p-6 flex flex-col gap-5 w-full max-w-[400px] mx-auto shadow-xl backdrop-blur-xl border border-blue-500/10">
      {/* Stats Bar */}
      <div className="flex justify-between text-xs text-neutral-300 mb-1 font-medium">
        <div>
          <div className="flex items-center gap-1"><TrendingUp size={14} className="text-blue-400" /> 5m Vol</div>
          <div className="text-lg text-white font-bold">$0</div>
        </div>
        <div className="text-center">
          <div className="flex items-center gap-1"><DollarSign size={13} className="text-green-400" /> Buys</div>
          <div className="text-green-400 font-semibold">0 / $0</div>
        </div>
        <div className="text-center">
          <div className="flex items-center gap-1"><DollarSign size={13} className="text-red-400" /> Sells</div>
          <div className="text-red-400 font-semibold">0 / $0</div>
        </div>
        <div className="text-right">
          <div className="flex items-center gap-1"><Zap size={13} className="text-yellow-400" /> Net Vol.</div>
          <div className="text-red-400 font-semibold">-$0</div>
        </div>
      </div>
      <div className="flex gap-1 mb-3">
        <div className="h-1 rounded bg-gradient-to-r from-green-400 to-blue-400" style={{ width: '60%' }}></div>
        <div className="h-1 rounded bg-gradient-to-r from-red-400 to-purple-400" style={{ width: '40%' }}></div>
      </div>
      {/* Buy/Sell Toggle */}
      <div className="flex gap-2 mb-2">
        <button
          className={`flex-1 py-2 rounded-lg font-semibold text-base transition-all border ${side === 'buy' ? 'bg-gradient-to-r from-green-300 to-blue-200 text-black border-green-300 ring-1 ring-green-200' : 'bg-[#23262F]/80 text-white border-zinc-700 hover:bg-green-400/10'}`}
          style={{ minWidth: 0 }}
          onClick={() => setSide('buy')}
        >
          Buy
        </button>
        <button
          className={`flex-1 py-2 rounded-lg font-semibold text-base transition-all border ${side === 'sell' ? 'bg-gradient-to-r from-red-400 to-purple-400 text-white border-red-300 ring-1 ring-red-200' : 'bg-[#23262F]/80 text-white border-zinc-700 hover:bg-red-400/10'}`}
          style={{ minWidth: 0 }}
          onClick={() => setSide('sell')}
        >
          Sell
        </button>
      </div>
      {/* Order Type Tabs */}
      <div className="flex gap-4 mb-2 text-sm font-semibold">
        {['market', 'limit', 'adv'].map(type => (
          <button
            key={type}
            className={`pb-1 border-b-2 transition-all ${orderType === type ? 'border-white text-white' : 'border-transparent text-neutral-400 hover:text-blue-400'}`}
            onClick={() => setOrderType(type)}
          >
            {type === 'market' ? 'Market' : type === 'limit' ? 'Limit' : 'Adv.'}
          </button>
        ))}
        <div className="ml-auto flex gap-2 items-center">
          <span className="bg-[#23262F] px-2 py-0.5 rounded text-xs font-mono">1</span>
          <span className="bg-gradient-to-r from-blue-400 to-purple-400 px-2 py-0.5 rounded text-xs font-mono">0.01</span>
        </div>
      </div>
      {/* Amount Input & Quick Select */}
      <div className="bg-gradient-to-br from-[#23262F]/80 via-[#181A20]/90 to-[#23262F]/80 rounded-xl p-4 mb-2 border border-blue-500/10 shadow">
        <div className="text-xs text-neutral-400 mb-2 font-semibold flex items-center gap-2">
          <Info size={14} className="text-blue-400" />AMOUNT
          <span className="text-white ml-2 font-semibold text-base">{amount}</span>
        </div>
        <div className="grid grid-cols-5 gap-2 mb-2">
          {quickAmounts.map((amt) => (
            <button
              key={amt}
              className="bg-[#23262F] rounded-full px-3 py-1 text-white font-semibold text-sm flex items-center justify-center hover:bg-blue-500/20 transition-all"
              onClick={() => setAmount(amt)}
            >
              <Plus size={13} className="mr-1 text-blue-400" />{amt}
            </button>
          ))}
          <button className="bg-[#23262F] rounded-full px-3 py-1 text-white flex items-center justify-center hover:bg-green-400/20 transition-all">
            <svg width="16" height="16" fill="none" stroke="currentColor" strokeWidth="2"><path d="M4 8h8M8 4v8"/></svg>
          </button>
        </div>
        <div className="flex items-center gap-3 text-xs text-yellow-300 mb-2 font-medium">
          <span className="flex items-center gap-1"><Zap size={13} /> 20%</span>
          <span className="flex items-center gap-1"><User size={13} /> 0.001</span>
          <span className="flex items-center gap-1"><Star size={13} /> 0.001</span>
          <span className="flex items-center gap-1">⚠️</span>
          <span className="text-neutral-400">Off</span>
        </div>
        <div className="flex items-center gap-2 mb-2">
          <input type="checkbox" className="accent-blue-500 scale-110" id="adv-strat" />
          <label htmlFor="adv-strat" className="text-xs text-white font-medium">Advanced Trading Strategy</label>
        </div>
        <button
          className={`w-full py-2 rounded-full font-bold text-lg mt-2 transition-all relative overflow-hidden ${side === 'buy' ? 'bg-gradient-to-r from-green-300 via-blue-200 to-green-400 text-black' : 'bg-gradient-to-r from-red-400 via-purple-400 to-red-500 text-white'}`}
          style={{ boxShadow: side === 'buy' ? '0 0 8px 1px #6ee7b7cc' : '0 0 8px 1px #f87171cc' }}
        >
          {side === 'buy' ? `Buy ${tokenName}` : `Sell ${tokenName}`}
        </button>
      </div>
      {/* Summary Bar */}
      <div className="flex justify-between text-xs text-neutral-400 mb-1 font-medium">
        <div className="text-center flex-1">
          <div>Bought</div>
          <div className="text-green-400 font-semibold">$0</div>
        </div>
        <div className="text-center flex-1">
          <div>Sold</div>
          <div className="text-red-400 font-semibold">$0</div>
        </div>
        <div className="text-center flex-1">
          <div>Holding</div>
          <div className="text-white font-semibold">$0</div>
        </div>
        <div className="text-center flex-1">
          <div>PnL</div>
          <div className="text-green-400 font-semibold">+$0 (+0%)</div>
        </div>
      </div>
      {/* Preset Tabs */}
      <div className="flex gap-2 mb-1">
        {PRESETS.map((p, i) => (
          <button
            key={p}
            className={`flex-1 py-1 rounded-lg font-medium transition-all ${preset === i ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white' : 'bg-[#23262F] text-white hover:bg-blue-500/20'}`}
            onClick={() => setPreset(i)}
          >
            {p}
          </button>
        ))}
      </div>
      {/* Token Info Section */}
      <div className="bg-gradient-to-br from-[#23262F]/80 via-[#181A20]/90 to-[#23262F]/80 rounded-xl p-4 border border-blue-500/10 shadow">
        <div className="flex items-center gap-2 mb-2">
          <span className="text-white font-semibold text-sm">Token Info</span>
          <svg width="16" height="16" fill="none" stroke="currentColor" strokeWidth="2"><path d="M6 9l2 2 4-4"/></svg>
        </div>
        <div className="grid grid-cols-3 gap-3">
          {['Top 10 H.', 'Dev H.', 'Snipers H.', 'Other', 'Another', 'Liquidity'].map((label, idx) => (
            <div key={label} className="bg-[#23262F] rounded-xl p-3 text-center">
              <div className={`font-semibold text-lg ${idx === 5 ? 'text-green-400' : 'text-green-400'}`}>{idx === 5 ? '100%' : '0%'}</div>
              <div className="text-xs text-neutral-400 mt-1">{label}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
